import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";
import MKBox from "components/MKBox";
import MKTypography from "components/MKTypography";
import MKButton from "components/MKButton";
import PlayArrowIcon from "@mui/icons-material/PlayArrow";
import MonetizationOnIcon from "@mui/icons-material/MonetizationOn";
import SearchIcon from "@mui/icons-material/Search";
import SchoolIcon from "@mui/icons-material/School";
import SportsIcon from "@mui/icons-material/Sports";
import { TypeAnimation } from "react-type-animation";
import Header from "components/Header";
import Footer from "components/Footer";
import { useState, useEffect } from "react";
import Dialog from "@mui/material/Dialog";
import Avatar from "@mui/material/Avatar";
import CircularProgress from "@mui/material/CircularProgress";
import { Link } from "react-router-dom";
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';
import IconButton from '@mui/material/IconButton';
import CloseIcon from '@mui/icons-material/Close';

function HomePage() {
  const [videoOpen, setVideoOpen] = useState(false);
  const [sponsoredPlayers, setSponsoredPlayers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedPlayer, setSelectedPlayer] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [featuredPlayers, setFeaturedPlayers] = useState([]);

  const handleVideoOpen = () => setVideoOpen(true);
  const handleVideoClose = () => setVideoOpen(false);

  const handlePlayerClick = (player) => {
    setSelectedPlayer(player);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedPlayer(null);
  };

  const solutions = [
    {
      icon: <SportsIcon />,
      title: "Talent Identification & Development Camps",
      description: "Scouting and training programs that equip players with elite-level skills.",
    },
    {
      icon: <SchoolIcon />,
      title: "Academic Support",
      description: "Helping athletes meet the educational requirements for scholarships.",
    },
    {
      icon: <MonetizationOnIcon />,
      title: "Scholarships & Financial Assistance",
      description: "Covering registration fees, travel expenses, and essential resources.",
    },
  ];

  const getInitials = (name) => {
    return name
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase();
  };

  useEffect(() => {
    const fetchPlayers = async () => {
      try {
        setIsLoading(true);
        const response = await fetch(
          "https://x8ki-letl-twmt.n7.xano.io/api:TF3YOouP/kbfoundation_players"
        );
        if (!response.ok) {
          throw new Error("Failed to fetch players");
        }
        const data = await response.json();

        // Get only sponsored players with images
        const sponsoredPlayersWithImages = data.filter(
          (player) => player.Sponsor === true && player.image && player.image.url
        );

        // Shuffle array to get random players
        const shuffled = sponsoredPlayersWithImages.sort(() => 0.5 - Math.random());

        // Get exactly 11 players
        setFeaturedPlayers(shuffled.slice(0, 11));
      } catch (error) {
        console.error("Error fetching players:", error);
        setFeaturedPlayers([]);
      } finally {
        setIsLoading(false);
      }
    };

    fetchPlayers();
  }, []);

  const TacticalBoard = () => {
    // Group players by position for a 4-4-2 formation
    const formation = {
      // 1 Goalkeeper
      GK: featuredPlayers.filter(p => 
        p.position?.toLowerCase() === "gk" || 
        p.position?.toLowerCase() === "goalkeeper"
      ).slice(0, 1),

      // 4 Defenders
      DEF: featuredPlayers.filter(p => {
        const pos = p.position?.toLowerCase() || '';
        return ['cb', 'rb', 'lb', 'defender', 'fb'].includes(pos);
      }).slice(0, 4),

      // 4 Midfielders
      MID: featuredPlayers.filter(p => {
        const pos = p.position?.toLowerCase() || '';
        return ['cdm', 'cm', 'cam', 'midfielder', 'dm', 'winger', 'rw', 'lw'].includes(pos);
      }).slice(0, 4),

      // 2 Forwards
      FWD: featuredPlayers.filter(p => {
        const pos = p.position?.toLowerCase() || '';
        return ['st', 'cf', 'striker', 'forward', 'attacker'].includes(pos);
      }).slice(0, 2),
    };

    const PlayerCard = ({ player }) => (
      <MKBox
        onClick={() => handlePlayerClick(player)}
        sx={{
          position: 'relative',
          width: { xs: 60, md: 80 },
          height: { xs: 60, md: 80 },
          margin: '0 auto',
          cursor: 'pointer',
          transition: 'transform 0.2s',
          '&:hover': {
            transform: 'scale(1.1)',
          },
        }}
      >
        <Avatar
          src={player.image.url || ''}
          alt={player.fullName}
          sx={{
            width: '100%',
            height: '100%',
            border: '2px solid white',
            boxShadow: 2,
            bgcolor: 'error.main',
            fontSize: { xs: '1rem', md: '1.25rem' },
          }}
        >
          {getInitials(player.fullName)}
        </Avatar>
        <MKBox
          sx={{
            position: 'absolute',
            bottom: -20,
            left: '50%',
            transform: 'translateX(-50%)',
            backgroundColor: 'rgba(0,0,0,0.8)',
            color: '#fff',
            padding: '2px 8px',
            borderRadius: '10px',
            fontSize: { xs: '0.7rem', md: '0.8rem' },
            whiteSpace: 'nowrap',
            zIndex: 1,
          }}
        >
          {player.fullName}
          <MKTypography
            variant="caption"
            sx={{
              display: 'block',
              color: 'rgba(255,255,255,0.7)',
              fontSize: '0.6rem',
            }}
          >
            {player.position}
          </MKTypography>
        </MKBox>
      </MKBox>
    );

    return (
      <MKBox
        sx={{
          background: 'linear-gradient(0deg, #2d5a27 0%, #348f2d 100%)',
          borderRadius: '10px',
          padding: 3,
          margin: '40px 0',
          position: 'relative',
          minHeight: '400px',
          border: '5px solid #fff',
          boxShadow: 3,
          overflow: 'hidden',
        }}
      >
        {/* Field Markings */}
        <MKBox
          sx={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            '&::before': {
              // Half Circle
              content: '""',
              position: 'absolute',
              bottom: 0,
              left: '50%',
              transform: 'translateX(-50%)',
              width: '150px',
              height: '75px',
              border: '2px solid rgba(255,255,255,0.3)',
              borderRadius: '150px 150px 0 0',
              borderBottom: 'none',
            },
          }}
        >
          {/* Penalty Area */}
          <MKBox
            sx={{
              position: 'absolute',
              bottom: 0,
              left: '50%',
              transform: 'translateX(-50%)',
              width: '60%',
              height: '40%',
              border: '2px solid rgba(255,255,255,0.3)',
              borderBottom: 'none',
              '&::before': {
                content: '""',
                position: 'absolute',
                bottom: 0,
                left: '50%',
                transform: 'translateX(-50%)',
                width: '40%',
                height: '50%',
                border: '2px solid rgba(255,255,255,0.3)',
                borderBottom: 'none',
              },
            }}
          />
        </MKBox>

        {/* Player Positions */}
        <Grid container sx={{ height: '100%', position: 'relative', zIndex: 1 }}>
          {/* Forwards - Top Row */}
          <Grid item xs={12} sx={{ height: '20%', display: 'flex', justifyContent: 'center' }}>
            <Grid container spacing={2} justifyContent="center">
              {formation.FWD.map((player, index) => (
                <Grid item xs={3} key={player.id} sx={{ display: 'flex', justifyContent: 'center' }}>
                  <PlayerCard player={player} />
                </Grid>
              ))}
            </Grid>
          </Grid>

          {/* Midfielders */}
          <Grid item xs={12} sx={{ height: '20%', display: 'flex', alignItems: 'center' }}>
            <Grid container spacing={2} justifyContent="space-around">
              {formation.MID.map((player, index) => (
                <Grid item xs={3} key={player.id}>
                  <PlayerCard player={player} />
                </Grid>
              ))}
            </Grid>
          </Grid>

          {/* Defenders */}
          <Grid item xs={12} sx={{ height: '20%', display: 'flex', alignItems: 'center' }}>
            <Grid container spacing={2} justifyContent="space-around">
              {formation.DEF.map((player, index) => (
                <Grid item xs={3} key={player.id}>
                  <PlayerCard player={player} />
                </Grid>
              ))}
            </Grid>
          </Grid>

          {/* Goalkeeper - Bottom Row */}
          <Grid item xs={12} sx={{ height: '20%', display: 'flex', alignItems: 'flex-end', justifyContent: 'center' }}>
            {formation.GK.map((player, index) => (
              <Grid item xs={3} key={player.id} sx={{ display: 'flex', justifyContent: 'center' }}>
                <PlayerCard player={player} />
              </Grid>
            ))}
          </Grid>
        </Grid>

        {isLoading && (
          <MKBox
            sx={{
              position: 'absolute',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              zIndex: 2,
            }}
          >
            <CircularProgress sx={{ color: 'white' }} />
          </MKBox>
        )}
      </MKBox>
    );
  };

  const PlayerDetailModal = () => {
    if (!selectedPlayer) return null;

    const details = [
      { label: "Full Name", value: selectedPlayer.fullName },
      { label: "Position", value: selectedPlayer.position },
      { label: "Date of Birth", value: selectedPlayer.DOB },
      { label: "Education", value: selectedPlayer.education },
      { label: "Shirt Size", value: selectedPlayer.shirtSize },
      { label: "Registration Date", value: new Date(selectedPlayer.created_at).toLocaleDateString() },
    ];

    return (
      <Dialog 
        open={isModalOpen} 
        onClose={handleCloseModal}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>
          <MKBox display="flex" alignItems="center" justifyContent="space-between">
            <MKBox display="flex" alignItems="center" gap={2}>
              <Avatar 
                src={selectedPlayer.image?.url}
                sx={{ 
                  width: 56,
                  height: 56,
                  bgcolor: 'error.main',
                  '& img': {
                    objectFit: 'cover',
                    objectPosition: 'center top',
                    width: '100%',
                    height: '100%',
                  }
                }}
              >
                {!selectedPlayer.image && getInitials(selectedPlayer.fullName)}
              </Avatar>
              <MKBox>
                <MKTypography variant="h6">{selectedPlayer.fullName}</MKTypography>
                <MKTypography variant="caption" color="text.secondary">
                  {selectedPlayer.position}
                </MKTypography>
              </MKBox>
            </MKBox>
            <IconButton onClick={handleCloseModal}>
              <CloseIcon />
            </IconButton>
          </MKBox>
        </DialogTitle>
        <DialogContent dividers>
          <Grid container spacing={2}>
