import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Container from "@mui/material/Container";
import Card from "@mui/material/Card";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import MKBox from "components/MKBox";
import MKTypography from "components/MKTypography";
import Header from "components/Header";

function Dashboard() {
  const navigate = useNavigate();
  const [payments, setPayments] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const authToken = localStorage.getItem("authToken");
    if (!authToken) {
      navigate("/auth");
      return;
    }

    const fetchPayments = async () => {
      try {
        const response = await fetch(
          "https://x8ki-letl-twmt.n7.xano.io/api:TF3YOouP/kbfoundation_players",
          {
            headers: {
              Authorization: `Bearer ${authToken}`,
            },
          }
        );

        if (!response.ok) {
          throw new Error("Failed to fetch payments");
        }

        const data = await response.json();
        setPayments(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchPayments();
  }, [navigate]);

  return (
    <>
      <Header />
      <MKBox
        minHeight="100vh"
        width="100%"
        sx={{
          backgroundColor: "#f8f9fa",
          paddingTop: "70px",
          paddingBottom: "30px",
        }}
      >
        <Container>
          <MKTypography variant="h3" mb={4}>
            Payment Dashboard
          </MKTypography>
          <Card>
            {isLoading ? (
              <MKBox p={3} textAlign="center">
                Loading payments...
              </MKBox>
            ) : error ? (
              <MKBox p={3} textAlign="center" color="error">
                {error}
              </MKBox>
            ) : (
              <TableContainer>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Player Name</TableCell>
                      <TableCell>Email</TableCell>
                      <TableCell>Amount</TableCell>
                      <TableCell>Status</TableCell>
                      <TableCell>Date</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {payments.map((payment) => (
                      <TableRow key={payment.id}>
                        <TableCell>{payment.player_name}</TableCell>
                        <TableCell>{payment.email}</TableCell>
                        <TableCell>GHS {payment.amount}</TableCell>
                        <TableCell>
                          <MKTypography
                            variant="caption"
                            color={payment.status === "successful" ? "success" : "error"}
                          >
                            {payment.status}
                          </MKTypography>
                        </TableCell>
                        <TableCell>
                          {new Date(payment.created_at).toLocaleDateString()}
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            )}
          </Card>
        </Container>
      </MKBox>
    </>
  );
}

export default Dashboard;
