import { useState } from 'react';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';
import IconButton from '@mui/material/IconButton';
import CloseIcon from '@mui/icons-material/Close';
import FormControl from "@mui/material/FormControl";
import InputLabel from "@mui/material/InputLabel";
import Select from "@mui/material/Select";
import MenuItem from "@mui/material/MenuItem";
import TextField from '@mui/material/TextField';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import Snackbar from '@mui/material/Snackbar';
import Alert from '@mui/material/Alert';

function SignupLinkModal({ open, onClose, generatedLink, setGeneratedLink }) {
  const [linkType, setLinkType] = useState("");
  const [isGeneratingLink, setIsGeneratingLink] = useState(false);
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState("");
  const [snackbarSeverity, setSnackbarSeverity] = useState("success");

  const handleGenerateLink = async () => {
    try {
      setIsGeneratingLink(true);
      const response = await fetch(
        "https://x8ki-letl-twmt.n7.xano.io/api:LfeuGUZr/generate-signup-link",
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            type: linkType
          })
        }
      );

      if (!response.ok) {
        throw new Error("Failed to generate signup link");
      }

      const data = await response.json();

      // Get the link from the response data
      const link = data.result1 || data.url || data.link;

      if (!link) {
        throw new Error("No link received from API");
      }

      // Set the full URL including the base URL if needed
      const baseUrl = window.location.origin;
      const fullLink = link.startsWith('http') ? link : `${baseUrl}/signup/${link}`;
      setGeneratedLink(fullLink);

      setSnackbarMessage("Link generated successfully!");
      setSnackbarSeverity("success");
      setSnackbarOpen(true);
    } catch (error) {
      setSnackbarMessage("Failed to generate link. Please try again.");
      setSnackbarSeverity("error");
      setSnackbarOpen(true);
    } finally {
      setIsGeneratingLink(false);
    }
  };

  const handleCopyLink = () => {
    navigator.clipboard.writeText(generatedLink).then(
      () => {
        setSnackbarMessage("Link copied to clipboard!");
        setSnackbarSeverity("success");
        setSnackbarOpen(true);
      },
      () => {
        setSnackbarMessage("Failed to copy link");
        setSnackbarSeverity("error");
        setSnackbarOpen(true);
      }
    );
  };

  const handleClose = () => {
    setLinkType("");
    setGeneratedLink("");
    onClose();
  };

  const handleSnackbarClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    setSnackbarOpen(false);
  };

  return (
    <>
      <Dialog
        open={open}
        onClose={handleClose}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>
          <Box display="flex" alignItems="center" justifyContent="space-between">
            <Typography variant="h6">Generate Signup Link</Typography>
            <IconButton onClick={handleClose}>
              <CloseIcon />
            </IconButton>
          </Box>
        </DialogTitle>
        <DialogContent dividers>
          <Box mb={3}>
            <FormControl fullWidth variant="standard">
              <InputLabel>Link Type</InputLabel>
              <Select
                value={linkType}
                onChange={(e) => setLinkType(e.target.value)}
                disabled={isGeneratingLink}
              >
                <MenuItem value="player">Player</MenuItem>
                <MenuItem value="sponsor">Sponsor</MenuItem>
              </Select>
            </FormControl>
          </Box>

          <Typography variant="caption" color="text.secondary" mb={2}>
            Status: {isGeneratingLink ? 'Generating...' : generatedLink ? 'Link Generated' : 'Waiting'}
          </Typography>

          <Box mt={3}>
            <TextField
              fullWidth
              label="Generated Link"
              value={generatedLink}
              placeholder="Link will appear here after generation"
              disabled={!generatedLink}
              InputProps={{
                readOnly: true,
                endAdornment: generatedLink && (
                  <IconButton onClick={handleCopyLink} edge="end" size="small">
                    <ContentCopyIcon />
                  </IconButton>
                ),
              }}
            />
            {generatedLink && (
              <Box mt={2} display="flex" justifyContent="center">
                <Button
                  variant="contained"
                  color="primary"
                  onClick={handleCopyLink}
                  startIcon={<ContentCopyIcon />}
                  fullWidth
                >
                  Copy to Clipboard
                </Button>
              </Box>
            )}
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="inherit">
            Cancel
          </Button>
          <Button
            onClick={handleGenerateLink}
            variant="contained"
            color="primary"
            disabled={!linkType || isGeneratingLink}
          >
            {isGeneratingLink ? (
              <CircularProgress size={24} color="inherit" />
            ) : (
              "Generate Link"
            )}
          </Button>
        </DialogActions>
      </Dialog>

      <Snackbar
        open={snackbarOpen}
        autoHideDuration={6000}
        onClose={handleSnackbarClose}
      >
        <Alert onClose={handleSnackbarClose} severity={snackbarSeverity} sx={{ width: '100%' }}>
          {snackbarMessage}
        </Alert>
      </Snackbar>
    </>
  );
}

export default SignupLinkModal;
