import { useState, useEffect } from "react";
import { loadStripe } from "@stripe/stripe-js";
import ReCAPTCHA from "react-google-recaptcha";
import xss from "xss";
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";
import MKBox from "components/MKBox";
import MKTypography from "components/MKTypography";
import MKButton from "components/MKButton";
import MKInput from "components/MKInput";
import FormControlLabel from "@mui/material/FormControlLabel";
import Checkbox from "@mui/material/Checkbox";
import Header from "components/Header";
import Footer from "components/Footer";

const stripePromise = loadStripe('your_publishable_key');

function BecomeSponsor() {
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    phoneNumber: "",
    emailUpdates: false,
    sponsorNumber: "",
  });
  const [recaptchaToken, setRecaptchaToken] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Sanitize inputs
  const sanitizeInput = (input) => {
    return xss(input.trim());
  };

  // Validate form data
  const validateForm = () => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phoneRegex = /^\+?[\d\s-]{8,}$/;

    if (!emailRegex.test(formData.email)) {
      throw new Error("Invalid email address");
    }
    if (!phoneRegex.test(formData.phoneNumber)) {
      throw new Error("Invalid phone number");
    }
    if (!recaptchaToken) {
      throw new Error("Please complete the reCAPTCHA");
    }
  };

  const handleChange = (e) => {
    const value = e.target.type === "checkbox" ? e.target.checked : sanitizeInput(e.target.value);
    setFormData({
      ...formData,
      [e.target.name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isSubmitting) return;

    try {
      setIsSubmitting(true);
      validateForm();

      // Create payment intent
      const response = await fetch("/api/create-payment-intent", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "X-CSRF-Token": document.querySelector('meta[name="csrf-token"]').content,
        },
        body: JSON.stringify({
          ...formData,
          recaptchaToken,
        }),
      });

      if (!response.ok) {
        throw new Error("Failed to create payment session");
      }

      const { clientSecret } = await response.json();

      // Initialize Stripe payment
      const stripe = await stripePromise;
      const { error } = await stripe.confirmPayment({
        clientSecret,
        elements: {
          payment_method: {
            card: elements.getElement('card'),
          },
        },
        confirmParams: {
          return_url: `${window.location.origin}/payment-success`,
        },
      });

      if (error) {
        throw new Error(error.message);
      }

    } catch (error) {
      alert(error.message);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <>
      <Header />
      <MKBox sx={{ paddingTop: "60px" }} bgcolor="white">
        {/* Hero Section */}
        <MKBox
          minHeight="50vh"
          width="100%"
          sx={{
            backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)), url('/images/soccer-feet.jpg')`,
            backgroundSize: "cover",
            backgroundPosition: "center",
            display: "grid",
            placeItems: "center",
          }}
        >
          <Container>
            <Grid container justifyContent="center" textAlign="center">
              <Grid item xs={12} md={8}>
                <MKTypography variant="h1" color="white" mb={3}>
                  Become a Sponsor
                </MKTypography>
                <MKTypography variant="body1" color="white" mb={3}>
                  Help transform young lives through soccer
                </MKTypography>
              </Grid>
            </Grid>
          </Container>
        </MKBox>

        {/* Sponsorship Form */}
        <Container sx={{ py: 6 }}>
          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <MKTypography variant="h3" mb={3}>
                Become a Sponsor
              </MKTypography>
              <MKTypography variant="body1" color="text" mb={3}>
                Your support can change a young athlete&apos;s life. Fill out the form below to
                start your sponsorship journey.
              </MKTypography>
              <Card sx={{ p: 3 }}>
                <form onSubmit={handleSubmit}>
                  <MKBox mb={2}>
                    <MKInput
                      fullWidth
                      label="Full Name"
                      name="fullName"
                      value={formData.fullName}
                      onChange={handleChange}
                      required
                    />
                  </MKBox>
                  <MKBox mb={2}>
                    <MKInput
                      fullWidth
                      type="email"
                      label="Email"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      required
                    />
                  </MKBox>
                  <MKBox mb={2}>
                    <MKInput
                      fullWidth
                      label="Phone Number"
                      name="phoneNumber"
                      value={formData.phoneNumber}
                      onChange={handleChange}
                      required
                    />
                  </MKBox>
                  <MKBox mb={2}>
                    <MKInput
                      fullWidth
                      type="number"
                      label="Number of Players to Sponsor"
                      name="sponsorNumber"
                      value={formData.sponsorNumber}
                      onChange={handleChange}
                      required
                    />
                  </MKBox>
                  <MKBox mb={3}>
                    <FormControlLabel
                      control={
                        <Checkbox
                          name="emailUpdates"
                          checked={formData.emailUpdates}
                          onChange={handleChange}
                          color="error"
                        />
                      }
                      label="Receive email updates about sponsored players"
                    />
                  </MKBox>
                  <MKBox mb={2}>
                    <ReCAPTCHA
                      sitekey="your_recaptcha_site_key"
                      onChange={(token) => setRecaptchaToken(token)}
                    />
                  </MKBox>
                  <MKButton 
                    type="submit" 
                    variant="contained" 
                    color="error" 
                    fullWidth
                    disabled={isSubmitting || !recaptchaToken}
                  >
                    {isSubmitting ? "Processing..." : "Submit Application"}
                  </MKButton>
                </form>
              </Card>
            </Grid>
            <Grid item xs={12} md={6}>
              {/* Sponsorship tiers stay the same */}
            </Grid>
          </Grid>
        </Container>
      </MKBox>
      <Footer />
    </>
  );
}

export default BecomeSponsor;
