import axios from 'axios';

const PAYSTACK_SECRET_KEY = process.env.REACT_APP_PAYSTACK_SECRET_KEY;

export const initiatePayment = async (data) => {
  try {
    console.log('Initiating payment with data:', data);

    const response = await axios.get(
      'https://x8ki-letl-twmt.n7.xano.io/api:2T4UiE5R/paystack_charge',
      {
        headers: {
          'Content-Type': 'application/json'
        },
        params: {
          email: data.email,
          provider: data.provider,
          phone: data.phone
        }
      }
    );

    console.log('Payment initiation response:', response.data);
    return response.data;
  } catch (error) {
    console.error('Payment initiation error:', {
      message: error.message,
      response: error.response?.data,
      status: error.response?.status
    });
    throw new Error(error.response?.data?.message || 'Payment initiation failed');
  }
};

export const submitOtp = async (otp, reference) => {
  try {
    console.log('Submitting OTP:', { otp, reference });

    const response = await axios.get(
      'https://x8ki-letl-twmt.n7.xano.io/api:2T4UiE5R/charge_otp',
      {
        headers: {
          'Content-Type': 'application/json'
        },
        params: {
          otp,
          reference
        }
      }
    );

    console.log('OTP submission response:', response.data);
    return response.data;
  } catch (error) {
    console.error('OTP submission error:', {
      message: error.message,
      response: error.response?.data,
      status: error.response?.status
    });
    throw new Error(error.response?.data?.message || 'OTP verification failed');
  }
};

export const verifyPayment = async (reference) => {
  try {
    console.log('Verifying payment with reference:', reference);

    const response = await axios.get(
      'https://x8ki-letl-twmt.n7.xano.io/api:2T4UiE5R/verify',
      {
        headers: {
          'Content-Type': 'application/json'
        },
        params: {
          reference
        }
      }
    );

    console.log('Payment verification response:', response.data);
    return response.data;
  } catch (error) {
    console.error('Payment verification error:', {
      message: error.message,
      response: error.response?.data,
      status: error.response?.status
    });
    throw new Error(error.response?.data?.message || 'Payment verification failed');
  }
};
