import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import MKBox from "components/MKBox";
import MKTypography from "components/MKTypography";
import MKButton from "components/MKButton";
import Header from "components/Header";
import Footer from "components/Footer";
import { useState } from "react";
import TextField from "@mui/material/TextField";
import MenuItem from "@mui/material/MenuItem";
import CircularProgress from "@mui/material/CircularProgress";

function BecomePlayer() {
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    DOB: "",
    position: "",
    phoneNumber: "",
    Channel: "",
    otherChannel: "",
    education: "",
    shirtSize: ""
  });

  const channels = ["Social Media", "A Coach", "Other"];
  const educationLevels = ["Wrote WASSCE", "Currently in SHS", "Completed SHS"];
  const shirtSizes = ["Small", "Medium", "Large"];

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await fetch('https://x8ki-letl-twmt.n7.xano.io/api:TF3YOouP/kbfoundation', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData)
      });
      if (response.ok) {
        alert('Application submitted successfully!');
        setFormData({
          fullName: "",
          email: "",
          DOB: "",
          position: "",
          phoneNumber: "",
          Channel: "",
          otherChannel: "",
          education: "",
          shirtSize: ""
        });
      }
    } catch (error) {
      alert('Error submitting application');
    }
    setLoading(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <>
      <Header />
      <MKBox sx={{ paddingTop: "60px" }} bgcolor="white">
        {/* Hero Section */}
        <MKBox
          minHeight="50vh"
          width="100%"
          sx={{
            backgroundImage: `linear-gradient(rgba(128, 0, 0, 0.7), rgba(128, 0, 0, 0.7)), url('/images/soccer-player.jpg')`,
            backgroundSize: "cover",
            backgroundPosition: "center",
            display: "grid",
            placeItems: "center",
          }}
        >
          <Container>
            <Grid container justifyContent="center" textAlign="center">
              <Grid item xs={12} md={8}>
                <MKTypography variant="h1" color="white" mb={3}>
                  Become a Player
                </MKTypography>
                <MKTypography variant="body1" color="white" mb={3}>
                  Join our program and take your soccer career to the next level
                </MKTypography>
              </Grid>
            </Grid>
          </Container>
        </MKBox>

        {/* Application Process */}
        <Container sx={{ py: 6 }}>
          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <MKTypography variant="h3" mb={3}>
                Application Process
              </MKTypography>
              <MKTypography variant="body1" color="text" mb={3}>
                Follow these steps to join our program:
              </MKTypography>
              {/* Add your application steps here */}
            </Grid>
            <Grid item xs={12} md={6}>
              <Card sx={{ p: 3 }}>
                <MKTypography variant="h4" mb={3}>
                  Apply Now
                </MKTypography>
                {/* Add your application form here */}
              </Card>
            </Grid>
          </Grid>
        </Container>

        {/* Application Form */}
        <Container sx={{ py: 6 }}>
          <Card sx={{ p: 4 }}>
            <MKTypography variant="h3" mb={3}>
              Player Application Form
            </MKTypography>
            <form onSubmit={handleSubmit}>
              <Grid container spacing={3}>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="Full Name"
                    name="fullName"
                    value={formData.fullName}
                    onChange={handleChange}
                    required
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    label="Email"
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    label="Date of Birth"
                    type="date"
                    name="DOB"
                    value={formData.DOB}
                    onChange={handleChange}
                    InputLabelProps={{ shrink: true }}
                    required
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    label="Position"
                    name="position"
                    value={formData.position}
                    onChange={handleChange}
                    required
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    label="Phone Number"
                    name="phoneNumber"
                    value={formData.phoneNumber}
                    onChange={handleChange}
                    required
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    select
                    fullWidth
                    label="How did you hear about us?"
                    name="Channel"
                    value={formData.Channel}
                    onChange={handleChange}
                    required
                  >
                    {channels.map((option) => (
                      <MenuItem key={option} value={option}>
                        {option}
                      </MenuItem>
                    ))}
                  </TextField>
                </Grid>
                {formData.Channel === "Other" && (
                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      label="Please specify"
                      name="otherChannel"
                      value={formData.otherChannel}
                      onChange={handleChange}
                      required
                    />
                  </Grid>
                )}
                <Grid item xs={12} md={6}>
                  <TextField
                    select
                    fullWidth
                    label="Education Level"
                    name="education"
                    value={formData.education}
                    onChange={handleChange}
                    required
                  >
                    {educationLevels.map((option) => (
                      <MenuItem key={option} value={option}>
                        {option}
                      </MenuItem>
                    ))}
                  </TextField>
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    select
                    fullWidth
                    label="Shirt Size"
                    name="shirtSize"
                    value={formData.shirtSize}
                    onChange={handleChange}
                    required
                  >
                    {shirtSizes.map((option) => (
                      <MenuItem key={option} value={option}>
                        {option}
                      </MenuItem>
                    ))}
                  </TextField>
                </Grid>
                <Grid item xs={12}>
                  <MKButton
                    type="submit"
                    variant="contained"
                    color="error"
                    fullWidth
                    disabled={loading}
                  >
                    {loading ? <CircularProgress size={24} color="inherit" /> : "Submit Application"}
                  </MKButton>
                </Grid>
              </Grid>
            </form>
          </Card>
        </Container>
      </MKBox>
      <Footer />
    </>
  );
}

export default BecomePlayer;
