import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Container from "@mui/material/Container";
import Card from "@mui/material/Card";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import MKBox from "components/MKBox";
import MKTypography from "components/MKTypography";
import Header from "components/Header";
import Avatar from "@mui/material/Avatar";
import Chip from "@mui/material/Chip";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";

function Dashboard() {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState(0);
  const [players, setPlayers] = useState([]);
  const [sponsors, setSponsors] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const authToken = localStorage.getItem("authToken");
    if (!authToken) {
      navigate("/auth");
      return;
    }

    const fetchData = async () => {
      try {
        setIsLoading(true);

        // Fetch players
        const playersResponse = await fetch(
          "https://x8ki-letl-twmt.n7.xano.io/api:TF3YOouP/kbfoundation_players",
          {
            headers: { Authorization: `Bearer ${authToken}` },
          }
        );

        // Fetch sponsors
        const sponsorsResponse = await fetch(
          "https://x8ki-letl-twmt.n7.xano.io/api:TF3YOouP/kbfoundation",
          {
            headers: { Authorization: `Bearer ${authToken}` },
          }
        );

        if (!playersResponse.ok || !sponsorsResponse.ok) {
          throw new Error("Failed to fetch data");
        }

        const playersData = await playersResponse.json();
        const sponsorsData = await sponsorsResponse.json();

        setPlayers(playersData);
        setSponsors(sponsorsData);
      } catch (err) {
        setError(err.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, [navigate]);

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  const getStatusColor = (paymentReference) => {
    return paymentReference ? "success" : "warning";
  };

  const getInitials = (name) => {
    return name
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase();
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  return (
    <>
      <Header />
      <MKBox
        minHeight="100vh"
        width="100%"
        sx={{
          backgroundColor: "#f8f9fa",
          paddingTop: "70px",
          paddingBottom: "30px",
        }}
      >
        <Container>
          <MKBox mb={4}>
            <Tabs value={activeTab} onChange={handleTabChange}>
              <Tab label="Players" />
              <Tab label="Sponsors" />
            </Tabs>
          </MKBox>

          <Card>
            {isLoading ? (
              <MKBox p={3} textAlign="center">Loading data...</MKBox>
            ) : error ? (
              <MKBox p={3} textAlign="center" color="error">{error}</MKBox>
            ) : activeTab === 0 ? (
              // Players Table
              <TableContainer>
                <Table>
                  <TableBody>
                    {players.map((player) => (
                      <TableRow 
                        key={player.id}
                        sx={{ 
                          '&:hover': { 
                            backgroundColor: 'rgba(0, 0, 0, 0.04)',
                            cursor: 'pointer'
                          },
                          borderBottom: '1px solid #eee'
                        }}
                      >
                        <TableCell>
                          <MKBox display="flex" alignItems="center">
                            <Avatar 
                              sx={{ 
                                bgcolor: 'error.main',
                                width: 40,
                                height: 40,
                                marginRight: 2
                              }}
                            >
                              {getInitials(player.fullName)}
                            </Avatar>
                            <MKBox>
                              <MKTypography variant="subtitle2" fontWeight="medium">
                                {player.fullName}
                              </MKTypography>
                              <MKTypography variant="caption" color="text.secondary">
                                {player.email}
                              </MKTypography>
                            </MKBox>
                          </MKBox>
                        </TableCell>
                        <TableCell>
                          <MKTypography variant="body2">{player.position}</MKTypography>
                        </TableCell>
                        <TableCell>
                          <Chip
                            label={player.paymentReference ? "Paid" : "Pending"}
                            color={getStatusColor(player.paymentReference)}
                            size="small"
                            sx={{
                              borderRadius: '16px',
                              fontSize: '0.75rem',
                              fontWeight: 600
                            }}
                          />
                        </TableCell>
                        <TableCell>
                          <MKTypography variant="body2">
                            {player.Channel}
                            {player.otherChannel && ` - ${player.otherChannel}`}
                          </MKTypography>
                        </TableCell>
                        <TableCell>
                          <MKTypography variant="body2">
                            {new Date(player.created_at).toLocaleDateString()}
                          </MKTypography>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            ) : (
              // Sponsors Table
              <TableContainer>
                <Table>
                  <TableBody>
                    {sponsors.map((sponsor) => (
                      <TableRow 
                        key={sponsor.id}
                        sx={{ 
                          '&:hover': { 
                            backgroundColor: 'rgba(0, 0, 0, 0.04)',
                            cursor: 'pointer'
                          },
                          borderBottom: '1px solid #eee'
                        }}
                      >
                        <TableCell>
                          <MKBox display="flex" alignItems="center">
                            <Avatar 
                              sx={{ 
                                bgcolor: 'primary.main',
                                width: 40,
                                height: 40,
                                marginRight: 2
                              }}
                            >
                              {getInitials(sponsor.fullName)}
                            </Avatar>
                            <MKBox>
                              <MKTypography variant="subtitle2" fontWeight="medium">
                                {sponsor.fullName}
                              </MKTypography>
                              <MKTypography variant="caption" color="text.secondary">
                                {sponsor.email}
                              </MKTypography>
                            </MKBox>
                          </MKBox>
                        </TableCell>
                        <TableCell>
                          <MKTypography variant="body2">
                            {sponsor.phoneNumber}
                            {sponsor.countryCode && ` (${sponsor.countryCode})`}
                          </MKTypography>
                        </TableCell>
                        <TableCell>
                          <MKTypography variant="body2">
                            {formatCurrency(sponsor.sponsorNumber)}
                          </MKTypography>
                        </TableCell>
                        <TableCell>
                          <Chip
                            label={sponsor.paymentReference ? "Paid" : "Pending"}
                            color={getStatusColor(sponsor.paymentReference)}
                            size="small"
                            sx={{
                              borderRadius: '16px',
                              fontSize: '0.75rem',
                              fontWeight: 600
                            }}
                          />
                        </TableCell>
                        <TableCell>
                          <MKTypography variant="body2">
                            {new Date(sponsor.created_at).toLocaleDateString()}
                          </MKTypography>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            )}
          </Card>
        </Container>
      </MKBox>
    </>
  );
}

export default Dashboard;
