                          label={({ name, percent }) => `${name} (${(percent * 100).toFixed(0)}%)`}
                          outerRadius={100}
                          fill="#8884d8"
                          dataKey="value"
                        >
                          {sponsorshipDistribution.map((entry, index) => (
                            <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                          ))}
                        </Pie>
                        <Tooltip />
                        <Legend />
                      </PieChart>
                    </ResponsiveContainer>
                  </Box>
                </Card>
              </Grid>

              {/* Recent Activity */}
              <Grid item xs={12} md={6}>
                <Card sx={{ p: 3, borderRadius: 2 }}>
                  <Typography variant="h6" sx={{ mb: 3 }}>
                    Recent Activity
                  </Typography>
                  <List>
                    {players.slice(-5).reverse().map((player) => (
                      <ListItem key={player.id} divider>
                        <ListItemIcon>
                          <Avatar sx={{ bgcolor: 'error.main' }}>
                            {getInitials(player.fullName)}
                          </Avatar>
                        </ListItemIcon>
                        <ListItemText
                          primary={player.fullName}
                          secondary={`Registered on ${new Date(player.created_at).toLocaleDateString()}`}
                        />
                        <Chip
                          label={player.paymentReference ? "Paid" : "Pending"}
                          color={player.paymentReference ? "success" : "warning"}
                          size="small"
                        />
                      </ListItem>
                    ))}
                  </List>
                </Card>
              </Grid>
            </Grid>
          </>
        );

      case 'players':
        return (
          <Card sx={{ p: 3, borderRadius: 2 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
              <Typography variant="h5">Players Management</Typography>
              <MKButton
                variant="contained"
                color="error"
                startIcon={<AddLinkIcon />}
                onClick={handleOpenSignupLinkModal}
              >
                Generate Player Link
              </MKButton>
            </Box>
            <div>
              <Box sx={{ mb: 3 }}>
                <Tabs
                  value={activePlayerSection}
                  onChange={(e, newValue) => setActivePlayerSection(newValue)}
                  sx={{
                    borderBottom: 1,
                    borderColor: "divider",
                    "& .MuiTab-root": {
                      textTransform: "none",
                      fontWeight: 600,
                    },
                  }}
                >
                  <Tab label="Regular Players" value="regular" />
                  <Tab label="Sponsored Players" value="sponsored" />
                </Tabs>
              </Box>

              {activePlayerSection === "regular" ? (
                <DataTable
                  table={{
                    columns: [
                      { Header: "Name", accessor: "name" },
                      { Header: "Email", accessor: "email" },
                      { Header: "Phone", accessor: "phone" },
                      { Header: "Status", accessor: "status" },
                      {
                        Header: "Actions",
                        accessor: "actions",
                        Cell: ({ row }) => (
                          <Box sx={{ display: "flex", gap: 1 }}>
                            <Button
                              variant="contained"
                              color="primary"
                              size="small"
                              onClick={() => handleSponsorAssignment(row.original)}
                            >
                              Assign Sponsor
                            </Button>
                            <Button
                              variant="contained"
                              color={row.original.status === "active" ? "error" : "success"}
                              size="small"
                              onClick={() => handleRegistrationToggle(row.original)}
                            >
                              {row.original.status === "active" ? "Deactivate" : "Activate"}
                            </Button>
                          </Box>
                        ),
                      },
                    ],
                    rows: players.filter(player => !player.sponsor).map((player) => ({
                      ...player,
                      actions: "",
                    })),
                  }}
                  entriesPerPage={false}
                  showTotalEntries={false}
                  showEntriesPerPage={false}
                  showTotalPages={false}
                  pagination={false}
                  search={false}
                  canSearch={false}
                  entriesPerPageOptions={[5, 10, 15, 20]}
                  sortBy="id"
                  sortOrder="desc"
                  noEndBorder
                />
              ) : (
                <DataTable
                  table={{
                    columns: [
                      { Header: "Name", accessor: "name" },
                      { Header: "Email", accessor: "email" },
                      { Header: "Phone", accessor: "phone" },
                      { Header: "Status", accessor: "status" },
                      { Header: "Sponsor", accessor: "sponsor_name" },
                      {
                        Header: "Actions",
                        accessor: "actions",
                        Cell: ({ row }) => (
                          <Box sx={{ display: "flex", gap: 1 }}>
                            <Button
                              variant="contained"
                              color={row.original.status === "active" ? "error" : "success"}
                              size="small"
                              onClick={() => handleRegistrationToggle(row.original)}
                            >
                              {row.original.status === "active" ? "Deactivate" : "Activate"}
                            </Button>
                          </Box>
                        ),
                      },
                    ],
                    rows: players.filter(player => player.sponsor).map((player) => ({
                      ...player,
                      sponsor_name: player.sponsor?.name || "Unknown",
                      actions: "",
                    })),
                  }}
                  entriesPerPage={false}
                  showTotalEntries={false}
                  showEntriesPerPage={false}
                  showTotalPages={false}
                  pagination={false}
                  search={false}
                  canSearch={false}
                  entriesPerPageOptions={[5, 10, 15, 20]}
                  sortBy="id"
                  sortOrder="desc"
                  noEndBorder
                />
              )}
            </div>
          </Card>
        );

      case 'sponsors':
        return (
          <Card sx={{ p: 3, borderRadius: 2 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
              <Typography variant="h5">Sponsors Management</Typography>
              <MKButton
                variant="contained"
                color="error"
                startIcon={<AddLinkIcon />}
                onClick={handleOpenSignupLinkModal}
              >
                Generate Sponsor Link
              </MKButton>
            </Box>
              <DataTable 
                value={sponsors} 
                paginator 
              rows={10}
                filterDisplay={isMobile ? "menu" : "row"}
                stripedRows
                responsiveLayout="stack"
                breakpoint="960px"
                emptyMessage="No sponsors found."
                className="custom-datatable"
                onRowClick={(e) => handleRowClick(e.data)}
                rowClassName={() => 'cursor-pointer'}
              >
                <Column 
                  field="fullName" 
                  header="Sponsor" 
                  body={isMobile ? mobileNameBodyTemplate : nameBodyTemplate}
                  sortable 
                  filter
                  filterPlaceholder="Search by name"
                />
                {!isMobile && (
                  <Column 
                    field="phoneNumber" 
                    header="Phone Number" 
                    sortable 
                  />
                )}
                <Column 
                  field="sponsorNumber" 
                  header="Amount" 
                  body={sponsorAmountTemplate}
                  sortable
                />
                <Column 
                  field="paymentReference" 
                  header="Status" 
                  body={statusBodyTemplate}
                  sortable
                />
                {!isMobile && (
                  <Column 
                    field="created_at" 
                    header="Registration Date" 
                    body={dateBodyTemplate}
                    sortable
                  />
                )}
              </DataTable>
          </Card>
        );

      default:
        return null;
    }
  };

  if (!isAuthenticated) {
    return (
      <>
        <Header />
        <MKBox
          sx={{
            backgroundColor: "#f8f9fa",
            minHeight: '100vh',
            paddingTop: { xs: '60px', sm: '70px' },
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          <Container maxWidth="sm">
            <Card sx={{ p: 4 }}>
              <MKBox textAlign="center" mb={3}>
                <LockIcon color="error" sx={{ fontSize: 60, mb: 2 }} />
                <MKTypography variant="h4" mb={1}>
                  Dashboard Access
                </MKTypography>
                <MKTypography variant="body2" color="text">
                  Please enter your password to access the dashboard
                </MKTypography>
              </MKBox>

              <MKBox component="form" onSubmit={(e) => { e.preventDefault(); handlePasswordSubmit(); }}>
                <MKBox mb={3}>
                  <MKInput
                    type="password"
                    label="Password"
                    fullWidth
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    error={!!passwordError}
                    disabled={passwordAttempts >= 5}
                  />
                  {passwordError && (
                    <MKTypography variant="caption" color="error">
                      {passwordError}
                    </MKTypography>
                  )}
                </MKBox>
                <MKButton
                  variant="gradient"
                  color="error"
                  fullWidth
                  type="submit"
                  disabled={!password || passwordAttempts >= 5}
                >
                  Login
                </MKButton>
              </MKBox>
          </Card>
        </Container>
      </MKBox>
      </>
    );
  }

  return (
    <Box sx={{ display: 'flex' }}>
      {/* Mobile App Bar */}
      {isMobile && (
        <AppBar 
          position="fixed" 
          sx={{ 
            bgcolor: 'background.paper',
            boxShadow: 1,
          }}
        >
          <Toolbar>
            <IconButton
              color="inherit"
              edge="start"
              onClick={handleDrawerToggle}
            >
              <MenuIcon />
            </IconButton>
            <Typography variant="h6" sx={{ color: 'error.main', ml: 2 }}>
              KBF Dashboard
            </Typography>
          </Toolbar>
        </AppBar>
      )}

      {/* Sidebar */}
      <Box
        component="nav"
        sx={{
          width: { md: 250 },
          flexShrink: { md: 0 }
        }}
      >
        {isMobile ? (
          <Drawer
            variant="temporary"
            open={mobileOpen}
            onClose={handleDrawerToggle}
            ModalProps={{
              keepMounted: true,
            }}
            sx={{
              '& .MuiDrawer-paper': { width: 250 },
            }}
          >
            {sidebarContent}
          </Drawer>
        ) : (
          <Drawer
            variant="permanent"
            sx={{
              '& .MuiDrawer-paper': { width: 250, boxSizing: 'border-box' },
            }}
            open
          >
            {sidebarContent}
          </Drawer>
        )}
      </Box>

      {/* Main Content */}
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          width: { md: `calc(100% - 250px)` },
          mt: { xs: 8, md: 0 },
        }}
      >
        <Container maxWidth="xl">
          {renderMainContent()}
        </Container>
      </Box>

      {/* Modals */}
      <DetailModal />
      <SignupLinkModal />
      <Snackbar
        open={snackbarOpen}
        autoHideDuration={6000}
        onClose={handleCloseSnackbar}
      >
        <Alert onClose={handleCloseSnackbar} severity={snackbarSeverity} sx={{ width: '100%' }}>
          {snackbarMessage}
        </Alert>
      </Snackbar>
    </Box>
  );
}

const styles = `
  .cursor-pointer {
    cursor: pointer;
  }
  .cursor-pointer:hover {
    background-color: rgba(0, 0, 0, 0.04) !important;
  }
`;

const styleSheet = document.createElement("style");
styleSheet.innerText = styles;
document.head.appendChild(styleSheet);

export default Dashboard;
