import { useState, useEffect } from "react";
import { useParams, Navigate } from "react-router-dom";
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";
import MKBox from "components/MKBox";
import MKTypography from "components/MKTypography";
import CircularProgress from "@mui/material/CircularProgress";
import Alert from "@mui/material/Alert";
import Header from "components/Header";
import Footer from "components/Footer";
import BecomePlayer from "layouts/pages/BecomePlayer";

function SignupWithToken() {
  const { token } = useParams();
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [tokenInfo, setTokenInfo] = useState(null);
  const [redirectToHome, setRedirectToHome] = useState(false);

  useEffect(() => {
    const validateToken = async () => {
      if (!token) {
        setError("Invalid token");
        setIsLoading(false);
        return;
      }

      try {
        setIsLoading(true);

        const response = await fetch(
          `https://x8ki-letl-twmt.n7.xano.io/api:LfeuGUZr/validate-signup-token`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({ token })
          }
        );

        if (!response.ok) {
          throw new Error("Invalid or expired token");
        }

        const data = await response.json();

        // Only proceed if token is for a player
        if (data.type !== "player") {
          throw new Error("Invalid token type");
        }

        setTokenInfo(data);
      } catch (err) {
        setError(err.message);
        // Set a timeout to redirect to the home page
        setTimeout(() => {
          setRedirectToHome(true);
        }, 3000);
      } finally {
        setIsLoading(false);
      }
    };

    validateToken();
  }, [token]);

  if (redirectToHome) {
    return <Navigate to="/" />;
  }

  if (isLoading) {
    return (
      <>
        <Header />
        <MKBox
          minHeight="100vh"
          width="100%"
          sx={{
            backgroundColor: "#f8f9fa",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            pt: 8,
            pb: 6
          }}
        >
          <Container maxWidth="lg">
            <Grid container justifyContent="center">
              <Grid item xs={12} md={8} lg={6}>
                <Card sx={{ p: 4, textAlign: "center" }}>
                  <CircularProgress />
                  <MKTypography variant="h5" mt={2}>
                    Validating your signup link...
                  </MKTypography>
                </Card>
              </Grid>
            </Grid>
          </Container>
        </MKBox>
      </>
    );
  }

  if (error) {
    return (
      <>
        <Header />
        <MKBox
          minHeight="100vh"
          width="100%"
          sx={{
            backgroundColor: "#f8f9fa",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            pt: 8,
            pb: 6
          }}
        >
          <Container maxWidth="lg">
            <Grid container justifyContent="center">
              <Grid item xs={12} md={8} lg={6}>
                <Card sx={{ p: 4, textAlign: "center" }}>
                  <Alert severity="error" sx={{ mb: 3 }}>
                    {error}
                  </Alert>
                  <MKTypography variant="body1">
                    Redirecting to home page...
                  </MKTypography>
                </Card>
              </Grid>
            </Grid>
          </Container>
        </MKBox>
      </>
    );
  }

  // If token is valid, show the player form
  if (tokenInfo) {
    return <BecomePlayer token={token} />;
  }

  return null;
}

export default SignupWithToken;
