import { useState } from "react";
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";
import MKBox from "components/MKBox";
import MKTypography from "components/MKTypography";
import MKButton from "components/MKButton";
import MKInput from "components/MKInput";
import MenuItem from "@mui/material/MenuItem";
import Select from "@mui/material/Select";
import FormControl from "@mui/material/FormControl";
import InputLabel from "@mui/material/InputLabel";
import Header from "components/Header";
import Footer from "components/Footer";

function BecomePlayer() {
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    DOB: "",
    position: "",
    phonNumber: "",
    Channel: "",
    otherChannel: "",
    education: "",
    shirtSize: "",
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(
        "https://x8ki-letl-twmt.n7.xano.io/api:TF3YOouP/kbfoundation_players",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(formData),
        }
      );

      if (response.ok) {
        alert("Application submitted successfully!");
        setFormData({
          fullName: "",
          email: "",
          DOB: "",
          position: "",
          phonNumber: "",
          Channel: "",
          otherChannel: "",
          education: "",
          shirtSize: "",
        });
      } else {
        throw new Error("Failed to submit");
      }
    } catch (error) {
      alert("Failed to submit application. Please try again.");
    }
  };

  return (
    <>
      <Header />
      <MKBox sx={{ paddingTop: "60px" }} bgcolor="white">
        {/* Hero Section */}
        <MKBox
          minHeight="50vh"
          width="100%"
          sx={{
            backgroundImage: `linear-gradient(rgba(128, 0, 0, 0.7), rgba(128, 0, 0, 0.7)), url('/images/soccer-player.jpg')`,
            backgroundSize: "cover",
            backgroundPosition: "center",
            display: "grid",
            placeItems: "center",
          }}
        >
          <Container>
            <Grid container justifyContent="center" textAlign="center">
              <Grid item xs={12} md={8}>
                <MKTypography variant="h1" color="white" mb={3}>
                  Become a Player
                </MKTypography>
                <MKTypography variant="body1" color="white" mb={3}>
                  Join our program and take your soccer career to the next level
                </MKTypography>
              </Grid>
            </Grid>
          </Container>
        </MKBox>

        {/* Application Form */}
        <Container sx={{ py: 6 }}>
          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <MKTypography variant="h3" mb={3}>
                Application Process
              </MKTypography>
              <MKTypography variant="body1" color="text" mb={3}>
                Fill out the form to join our program. Make sure all information is accurate.
              </MKTypography>
            </Grid>
            <Grid item xs={12} md={6}>
              <Card sx={{ p: 3 }}>
                <form onSubmit={handleSubmit}>
                  <MKBox mb={2}>
                    <MKInput
                      fullWidth
                      label="Full Name"
                      name="fullName"
                      value={formData.fullName}
                      onChange={handleChange}
                      required
                    />
                  </MKBox>
                  <MKBox mb={2}>
                    <MKInput
                      fullWidth
                      type="email"
                      label="Email"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      required
                    />
                  </MKBox>
                  <MKBox mb={2}>
                    <MKInput
                      fullWidth
                      type="date"
                      label="Date of Birth"
                      name="DOB"
                      value={formData.DOB}
                      onChange={handleChange}
                      required
                    />
                  </MKBox>
                  <MKBox mb={2}>
                    <MKInput
                      fullWidth
                      label="Position"
                      name="position"
                      value={formData.position}
                      onChange={handleChange}
                      required
                    />
                  </MKBox>
                  <MKBox mb={2}>
                    <MKInput
                      fullWidth
                      label="Phone Number"
                      name="phonNumber"
                      value={formData.phonNumber}
                      onChange={handleChange}
                      required
                    />
                  </MKBox>
                  <MKBox mb={2}>
                    <FormControl fullWidth>
                      <InputLabel>How did you hear about us?</InputLabel>
                      <Select
                        name="Channel"
                        value={formData.Channel}
                        onChange={handleChange}
                        required
                      >
                        <MenuItem value="Social Media">Social Media</MenuItem>
                        <MenuItem value="A Coach">A Coach</MenuItem>
                        <MenuItem value="Other">Other</MenuItem>
                      </Select>
                    </FormControl>
                  </MKBox>
                  {formData.Channel === "Other" && (
                    <MKBox mb={2}>
                      <MKInput
                        fullWidth
                        label="Please specify"
                        name="otherChannel"
                        value={formData.otherChannel}
                        onChange={handleChange}
                      />
                    </MKBox>
                  )}
                  <MKBox mb={2}>
                    <FormControl fullWidth>
                      <InputLabel>Education Level</InputLabel>
                      <Select
                        name="education"
                        value={formData.education}
                        onChange={handleChange}
                        required
                      >
                        <MenuItem value="Wrote WASSCE">Wrote WASSCE</MenuItem>
                        <MenuItem value="Currently in SHS">Currently in SHS</MenuItem>
                        <MenuItem value="Completed SHS">Completed SHS</MenuItem>
                      </Select>
                    </FormControl>
                  </MKBox>
                  <MKBox mb={2}>
                    <FormControl fullWidth>
                      <InputLabel>Shirt Size</InputLabel>
                      <Select
                        name="shirtSize"
                        value={formData.shirtSize}
                        onChange={handleChange}
                        required
                      >
                        <MenuItem value="Small">Small</MenuItem>
                        <MenuItem value="Medium">Medium</MenuItem>
                        <MenuItem value="Large">Large</MenuItem>
                      </Select>
                    </FormControl>
                  </MKBox>
                  <MKButton type="submit" variant="contained" color="error" fullWidth>
                    Submit Application
                  </MKButton>
                </form>
              </Card>
            </Grid>
          </Grid>
        </Container>
      </MKBox>
      <Footer />
    </>
  );
}

export default BecomePlayer;
