          // Group players by position
          const groupPlayersByPosition = (playersList) => {
            return {
              'Forwards': playersList.filter(p => {
                const pos = p.position?.toLowerCase() || '';
                return ['st', 'cf', 'striker', 'forward', 'attacker', 'rw', 'lw'].includes(pos);
