            };
          };

          const sponsoredPositions = groupPlayersByPosition(sponsoredPlayers);
          const regularPositions = groupPlayersByPosition(regularPlayers);

          return (
            <Box>
              <Typography variant="h5" sx={{ mb: 3 }}>Players Management</Typography>

              {/* Sponsored Players Section */}
              <Box sx={{ mb: 4 }}>
                <Typography variant="h6" sx={{ mb: 2 }}>
                  Sponsored Players
                </Typography>
                <Grid container spacing={3}>
                  {Object.entries(sponsoredPositions).map(([position, positionPlayers]) => (
                    positionPlayers.length > 0 && (
                      <Grid item xs={12} md={6} lg={3} key={position}>
                        <Card sx={{ p: 3, height: '100%' }}>
                          <Box sx={{ 
                            display: 'flex', 
                            alignItems: 'center', 
                            mb: 2,
                            pb: 2,
                            borderBottom: 1,
                            borderColor: 'divider'
                          }}>
                            <Typography variant="h6">{position}</Typography>
                            <Chip 
                              label={`${positionPlayers.length} Player${positionPlayers.length !== 1 ? 's' : ''}`}
                              color="primary"
                              size="small"
                              sx={{ ml: 2 }}
                            />
                          </Box>
                          <Grid container spacing={2}>
                            {positionPlayers.map((player) => (
                              <Grid item xs={12} key={player.id}>
                                <Card 
                                  sx={{ 
                                    cursor: 'pointer',
                                    '&:hover': {
                                      bgcolor: 'action.hover',
                                      transform: 'translateY(-2px)',
                                      transition: 'transform 0.2s',
                                    },
                                    p: 2
                                  }}
                                  onClick={() => handleRowClick(player)}
                                >
                                  <Box display="flex" alignItems="center">
                                    <Avatar 
                                      sx={{ 
                                        bgcolor: 'error.main',
                                        width: 48,
                                        height: 48,
                                        mr: 2
                                      }}
                                    >
                                      {getInitials(player.fullName)}
                                    </Avatar>
                                    <Box flex={1}>
                                      <Typography variant="subtitle1" fontWeight="medium">
                                        {player.fullName}
                                      </Typography>
                                      <Typography variant="caption" color="text.secondary">
                                        {player.position}
                                      </Typography>
                                    </Box>
                                  </Box>
                                </Card>
                              </Grid>
                            ))}
                          </Grid>
                        </Card>
                      </Grid>
                    )
                  ))}
                </Grid>
              </Box>

              {/* Regular Players Section */}
              <Box sx={{ mt: 4 }}>
                <Typography variant="h6" sx={{ mb: 2 }}>
                  Regular Players
                </Typography>
                <Grid container spacing={3}>
                  {Object.entries(regularPositions).map(([position, positionPlayers]) => (
                    positionPlayers.length > 0 && (
                      <Grid item xs={12} md={6} lg={3} key={position}>
                        <Card sx={{ p: 3, height: '100%' }}>
                          <Box sx={{ 
                            display: 'flex', 
                            alignItems: 'center', 
                            mb: 2,
                            pb: 2,
                            borderBottom: 1,
                            borderColor: 'divider'
                          }}>
                            <Typography variant="h6">{position}</Typography>
                            <Chip 
                              label={`${positionPlayers.length} Player${positionPlayers.length !== 1 ? 's' : ''}`}
                              color="primary"
                              size="small"
                              sx={{ ml: 2 }}
                            />
                          </Box>
                          <Grid container spacing={2}>
                            {positionPlayers.map((player) => (
                              <Grid item xs={12} key={player.id}>
                                <Card 
                                  sx={{ 
                                    cursor: 'pointer',
                                    '&:hover': {
                                      bgcolor: 'action.hover',
                                      transform: 'translateY(-2px)',
                                      transition: 'transform 0.2s',
                                    },
                                    p: 2
                                  }}
                                  onClick={() => handleRowClick(player)}
                                >
                                  <Box display="flex" alignItems="center">
                                    <Avatar 
                                      sx={{ 
                                        bgcolor: 'primary.main',
                                        width: 48,
                                        height: 48,
                                        mr: 2
                                      }}
                                    >
                                      {getInitials(player.fullName)}
                                    </Avatar>
                                    <Box flex={1}>
                                      <Typography variant="subtitle1" fontWeight="medium">
                                        {player.fullName}
                                      </Typography>
                                      <Typography variant="caption" color="text.secondary">
                                        {player.position}
                                      </Typography>
                                    </Box>
                                  </Box>
                                </Card>
                              </Grid>
                            ))}
                          </Grid>
                        </Card>
                      </Grid>
                    )
                  ))}
                </Grid>
              </Box>
            </Box>
          );
        }

        console.log("Rendering admin players view with data:", players);
        return (
          <Card sx={{ p: 3, borderRadius: 2 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
              <Typography variant="h5">Players Management</Typography>
              {userRole === 'admin' && (
                <MKButton
                  variant="contained"
                  color="error"
                  startIcon={<AddLinkIcon />}
                  onClick={handleOpenSignupLinkModal}
                >
                  Generate Player Link
                </MKButton>
              )}
            </Box>

            {/* Sponsored Players Section */}
            <Box sx={{ mb: userRole === 'admin' ? 4 : 0 }}>
              <Typography variant="h6" sx={{ mb: 2 }}>
                {userRole === 'admin' ? 'Sponsored Players' : 'Sponsored Players'}
              </Typography>
              {/* Add debug logs before filtering */}
              {console.log("=== Admin View Debug Logs ===")}
              {console.log("1. Pre-filter players:", players)}
              {console.log("2. Sponsor field values:", players.map(p => ({
                name: p.fullName,
                sponsor: p.Sponsor,
                sponsorId: p.sponsorsID,
                paymentRef: p.paymentReference
              })))}
              <DataTable 
                value={players.filter(player => {
                  const isSponsored = player.Sponsor === true;
                  console.log(`3. Player ${player.fullName}: Sponsor=${player.Sponsor}, isSponsored=${isSponsored}`);
                  return isSponsored;
                })}
                paginator 
                rows={10}
                filterDisplay={isMobile ? "menu" : "row"}
                stripedRows
                responsiveLayout="stack"
                breakpoint="960px"
                emptyMessage="No sponsored players found."
                className="custom-datatable"
                onRowClick={(e) => handleRowClick(e.data)}
                rowClassName={() => 'cursor-pointer'}
              >
                <Column 
                  field="fullName" 
                  header="Player" 
                  body={isMobile ? mobileNameBodyTemplate : nameBodyTemplate}
                  sortable 
                  filter
                  filterPlaceholder="Search by name"
                />
                {!isMobile && (
                  <Column 
                    field="position" 
                    header="Position" 
                    sortable 
                  />
                )}
                {userRole === 'admin' && (
                  <Column 
                    field="Sponsor" 
                    header="Sponsor" 
                    body={sponsorNameTemplate}
                    sortable 
                  />
                )}
                <Column 
                  field="paymentReference" 
                  header="Status" 
                  body={statusBodyTemplate}
                  sortable
                />
                {!isMobile && (
                  <Column 
                    field="created_at" 
                    header="Registration Date" 
                    body={dateBodyTemplate}
                    sortable
                  />
                )}
              </DataTable>
            </Box>

            {/* Regular Players Section */}
            <Box sx={{ mt: 4 }}>
              <Typography variant="h6" sx={{ mb: 2 }}>
                Regular Players
              </Typography>
              {/* Add debug logs for regular players */}
              {console.log("=== Regular Players Debug Logs ===")}
              {console.log("1. Pre-filter players for regular table:", players)}
              <DataTable 
                value={players.filter(player => {
                  const isRegular = player.Sponsor !== true;
                  console.log(`2. Player ${player.fullName}: Sponsor=${player.Sponsor}, isRegular=${isRegular}`);
                  return isRegular;
                })}
                paginator 
                rows={10}
                filterDisplay={isMobile ? "menu" : "row"}
                stripedRows
                responsiveLayout="stack"
                breakpoint="960px"
                emptyMessage="No regular players found."
                className="custom-datatable"
                onRowClick={(e) => handleRowClick(e.data)}
                rowClassName={() => 'cursor-pointer'}
              >
                <Column 
                  field="fullName" 
                  header="Player" 
                  body={isMobile ? mobileNameBodyTemplate : nameBodyTemplate}
                  sortable 
                  filter
                  filterPlaceholder="Search by name"
                />
                {!isMobile && (
                  <Column 
                    field="position" 
                    header="Position" 
                    sortable 
                  />
                )}
                <Column 
                  field="paymentReference" 
                  header="Status" 
                  body={statusBodyTemplate}
                  sortable
                />
                {!isMobile && (
                  <Column 
                    field="created_at" 
                    header="Registration Date" 
                    body={dateBodyTemplate}
                    sortable
                  />
                )}
              </DataTable>
            </Box>
          </Card>
        );

      case 'sponsors':
        console.log("Rendering sponsors view with data:", sponsors);
        return (
          <Card sx={{ p: 3, borderRadius: 2 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
              <Typography variant="h5">Sponsors Management</Typography>
              <MKButton
                variant="contained"
                color="error"
                startIcon={<AddLinkIcon />}
                onClick={handleOpenSignupLinkModal}
              >
                Generate Sponsor Link
              </MKButton>
            </Box>
              <DataTable 
                value={sponsors} 
                paginator 
              rows={10}
                filterDisplay={isMobile ? "menu" : "row"}
                stripedRows
                responsiveLayout="stack"
                breakpoint="960px"
                emptyMessage="No sponsors found."
                className="custom-datatable"
                onRowClick={(e) => handleRowClick(e.data)}
                rowClassName={() => 'cursor-pointer'}
              >
                <Column 
                  field="fullName" 
                  header="Sponsor" 
                  body={isMobile ? mobileNameBodyTemplate : nameBodyTemplate}
                  sortable 
                  filter
                  filterPlaceholder="Search by name"
                />
                {!isMobile && (
                  <Column 
                    field="phoneNumber" 
                    header="Phone Number" 
                    sortable 
                  />
                )}
                <Column 
                  field="sponsorNumber" 
                  header="Amount" 
                  body={sponsorAmountTemplate}
                  sortable
                />
                <Column 
                  field="paymentReference" 
                  header="Status" 
                  body={statusBodyTemplate}
                  sortable
                />
                {!isMobile && (
                  <Column 
                    field="created_at" 
                    header="Registration Date" 
                    body={dateBodyTemplate}
                    sortable
                  />
                )}
              </DataTable>
          </Card>
        );

      default:
        return null;
    }
  };

  if (!isAuthenticated) {
    return (
      <>
        <Header />
        <MKBox
          sx={{
            backgroundColor: "#f8f9fa",
            minHeight: '100vh',
            paddingTop: { xs: '60px', sm: '70px' },
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          <Container maxWidth="sm">
            <Card sx={{ p: 4 }}>
              <MKBox textAlign="center" mb={3}>
                <LockIcon color="error" sx={{ fontSize: 60, mb: 2 }} />
                <MKTypography variant="h4" mb={1}>
                  Dashboard Access
                </MKTypography>
                <MKTypography variant="body2" color="text">
                  Please enter your password to access the dashboard
                </MKTypography>
              </MKBox>

              <MKBox component="form" onSubmit={(e) => { e.preventDefault(); handleLogin(); }}>
                <MKBox mb={3}>
                  <MKInput
                    type="password"
                    label="Password"
                    fullWidth
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </MKBox>
                {loginError && (
                  <MKTypography color="error" variant="caption" mb={2} display="block">
                    {loginError}
                  </MKTypography>
                )}
                <MKButton
                  variant="contained"
                  color="info"
                  fullWidth
                  type="submit"
                >
                  Access Dashboard
                </MKButton>
              </MKBox>
            </Card>
          </Container>
        </MKBox>
      </>
    );
  }

  return (
    <Box sx={{ display: 'flex' }}>
      {/* Mobile App Bar */}
      {isMobile && (
        <AppBar 
          position="fixed" 
          sx={{ 
            bgcolor: 'background.paper',
            boxShadow: 1,
          }}
        >
          <Toolbar>
            <IconButton
              color="inherit"
              edge="start"
              onClick={handleDrawerToggle}
            >
              <MenuIcon />
            </IconButton>
            <Typography variant="h6" sx={{ color: 'error.main', ml: 2 }}>
              KBF Dashboard
            </Typography>
          </Toolbar>
        </AppBar>
      )}

      {/* Sidebar */}
      <Box
        component="nav"
        sx={{
          width: { md: 250 },
          flexShrink: { md: 0 }
        }}
      >
        {isMobile ? (
          <Drawer
            variant="temporary"
            open={mobileOpen}
            onClose={handleDrawerToggle}
            ModalProps={{
              keepMounted: true,
            }}
            sx={{
              '& .MuiDrawer-paper': { width: 250 },
            }}
          >
            {sidebarContent}
          </Drawer>
        ) : (
          <Drawer
            variant="permanent"
            sx={{
              '& .MuiDrawer-paper': { width: 250, boxSizing: 'border-box' },
            }}
            open
          >
            {sidebarContent}
          </Drawer>
        )}
      </Box>

      {/* Main Content */}
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          width: { md: `calc(100% - 250px)` },
          mt: { xs: 8, md: 0 },
        }}
      >
        <Container maxWidth="xl">
          {renderMainContent()}
        </Container>
      </Box>

      {/* Modals */}
      <DetailModal />
      <SignupLinkModal />
      <Snackbar
        open={snackbarOpen}
        autoHideDuration={6000}
        onClose={handleCloseSnackbar}
      >
        <Alert onClose={handleCloseSnackbar} severity={snackbarSeverity} sx={{ width: '100%' }}>
          {snackbarMessage}
        </Alert>
      </Snackbar>
    </Box>
  );
}

const styles = `
  .cursor-pointer {
    cursor: pointer;
  }
  .cursor-pointer:hover {
    background-color: rgba(0, 0, 0, 0.04) !important;
  }
`;

const styleSheet = document.createElement("style");
styleSheet.innerText = styles;
document.head.appendChild(styleSheet);

export default Dashboard;
