import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Container from "@mui/material/Container";
import Card from "@mui/material/Card";
import MKBox from "components/MKBox";
import MKTypography from "components/MKTypography";
import Header from "components/Header";
import Avatar from "@mui/material/Avatar";
import Chip from "@mui/material/Chip";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import 'primereact/resources/themes/lara-light-indigo/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';
import useMediaQuery from '@mui/material/useMediaQuery';
import { useTheme } from '@mui/material/styles';
import MKButton from "components/MKButton";
import Grid from "@mui/material/Grid";
import Icon from "@mui/material/Icon";

function Dashboard() {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const isTablet = useMediaQuery(theme.breakpoints.down('md'));
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState(0);
  const [players, setPlayers] = useState([]);
  const [sponsors, setSponsors] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const authToken = localStorage.getItem("authToken");
    if (!authToken) {
      navigate("/auth");
      return;
    }

    const fetchData = async () => {
      try {
        setIsLoading(true);

        // Fetch players
        const playersResponse = await fetch(
          "https://x8ki-letl-twmt.n7.xano.io/api:TF3YOouP/kbfoundation_players",
          {
            headers: { Authorization: `Bearer ${authToken}` },
          }
        );

        // Fetch sponsors
        const sponsorsResponse = await fetch(
          "https://x8ki-letl-twmt.n7.xano.io/api:TF3YOouP/kbfoundation",
          {
            headers: { Authorization: `Bearer ${authToken}` },
          }
        );

        if (!playersResponse.ok || !sponsorsResponse.ok) {
          throw new Error("Failed to fetch data");
        }

        const playersData = await playersResponse.json();
        const sponsorsData = await sponsorsResponse.json();

        setPlayers(playersData);
        setSponsors(sponsorsData);
      } catch (err) {
        setError(err.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, [navigate]);

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  const getStatusColor = (paymentReference) => {
    return paymentReference ? "success" : "warning";
  };

  const getInitials = (name) => {
    return name
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase();
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const statusBodyTemplate = (rowData) => {
    return (
      <Chip
        label={rowData.paymentReference ? "Paid" : "Pending"}
        color={getStatusColor(rowData.paymentReference)}
        size="small"
        sx={{
          borderRadius: '16px',
          fontSize: '0.75rem',
          fontWeight: 600
        }}
      />
    );
  };

  const nameBodyTemplate = (rowData) => {
    return (
      <MKBox display="flex" alignItems="center">
        <Avatar 
          sx={{ 
            bgcolor: activeTab === 0 ? 'error.main' : 'primary.main',
            width: 40,
            height: 40,
            marginRight: 2
          }}
        >
          {getInitials(rowData.fullName)}
        </Avatar>
        <MKBox>
          <MKTypography variant="subtitle2" fontWeight="medium">
            {rowData.fullName}
          </MKTypography>
          <MKTypography variant="caption" color="text.secondary">
            {rowData.email}
          </MKTypography>
        </MKBox>
      </MKBox>
    );
  };

  const dateBodyTemplate = (rowData) => {
    return new Date(rowData.created_at).toLocaleDateString();
  };

  const amountBodyTemplate = (rowData) => {
    return formatCurrency(rowData.sponsorNumber);
  };

  const mobileNameBodyTemplate = (rowData) => {
    return (
      <MKBox display="flex" flexDirection="column" gap={1}>
        <MKBox display="flex" alignItems="center" gap={1}>
          <Avatar 
            sx={{ 
              bgcolor: activeTab === 0 ? 'error.main' : 'primary.main',
              width: 32,
              height: 32
            }}
          >
            {getInitials(rowData.fullName)}
          </Avatar>
          <MKTypography variant="subtitle2" fontWeight="medium">
            {rowData.fullName}
          </MKTypography>
        </MKBox>
        <MKBox pl={5}>
          <MKTypography variant="caption" color="text.secondary">
            {rowData.email}
          </MKTypography>
          {activeTab === 0 && (
            <MKTypography variant="caption" display="block" color="text.secondary">
              {rowData.position}
            </MKTypography>
          )}
        </MKBox>
      </MKBox>
    );
  };

  const getVisibleColumns = () => {
    if (isMobile) {
      return ['fullName', 'paymentReference'];
    }
    if (isTablet) {
      return ['fullName', 'paymentReference', 'created_at'];
    }
    return ['fullName', 'position', 'paymentReference', 'Channel', 'created_at'];
  };

  const customStyles = {
    header: {
      backgroundColor: theme.palette.background.paper,
      padding: theme.spacing(2),
      borderRadius: theme.shape.borderRadius,
      boxShadow: theme.shadows[2],
    },
    card: {
      overflow: 'hidden',
      border: 'none',
      boxShadow: theme.shadows[5],
      padding: theme.spacing(2),
    },
    tabs: {
      backgroundColor: theme.palette.background.paper,
      borderRadius: theme.shape.borderRadius,
      padding: theme.spacing(1),
      marginBottom: theme.spacing(3),
    },
    table: {
      '& .p-datatable-wrapper': {
        padding: theme.spacing(1),
      },
      '& .p-datatable-header': {
        padding: theme.spacing(2),
        backgroundColor: theme.palette.background.paper,
      },
      '& .p-datatable-tbody > tr > td': {
        padding: {
          xs: theme.spacing(1.5),
          sm: theme.spacing(2),
        },
      },
      '& .p-paginator': {
        padding: theme.spacing(2),
      },
      '& .p-datatable-footer': {
        padding: theme.spacing(2),
      },
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("isAuthenticated");
    navigate("/auth");
  };

  const statsCards = [
    {
      title: "Orders",
      value: players.length,
      subtitle: `${players.filter(p => p.created_at > Date.now() - 7*24*60*60*1000).length} new since last visit`,
      icon: "shopping_cart",
      color: "info"
    },
    {
      title: "Revenue",
      value: `$${sponsors.reduce((acc, s) => acc + (s.sponsorNumber || 0), 0).toLocaleString()}`,
      subtitle: "+52% since last week",
      icon: "paid",
      color: "success"
    },
    {
      title: "Customers",
      value: players.length + sponsors.length,
      subtitle: `${players.filter(p => p.created_at > Date.now() - 24*60*60*1000).length} newly registered`,
      icon: "person",
      color: "primary"
    },
    {
      title: "Comments",
      value: `${players.length} Unread`,
      subtitle: `${players.filter(p => p.created_at > Date.now() - 24*60*60*1000).length} responded`,
      icon: "chat",
      color: "error"
    }
  ];

  return (
    <>
      <Header />
      <MKBox
        sx={{
          backgroundColor: "#f8f9fa",
          minHeight: '100vh',
          paddingTop: { xs: '60px', sm: '70px' },
          paddingBottom: { xs: '20px', sm: '30px' },
        }}
      >
        <Container maxWidth="xl">
          {/* Stats Cards */}
          <Grid container spacing={3} mb={4}>
            {statsCards.map((card, index) => (
              <Grid item xs={12} sm={6} md={3} key={index}>
                <Card sx={{ p: 3 }}>
                  <MKBox display="flex" justifyContent="space-between" alignItems="center">
                    <MKBox>
                      <MKTypography variant="button" color="text" fontWeight="medium">
                        {card.title}
                      </MKTypography>
                      <MKTypography variant="h4" fontWeight="bold" mt={1} mb={2}>
                        {card.value}
                      </MKTypography>
                      <MKTypography variant="caption" color="text" sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
                        {card.subtitle}
                      </MKTypography>
                    </MKBox>
                    <MKBox
                      sx={{
                        width: 48,
                        height: 48,
                        borderRadius: '50%',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        backgroundColor: `${card.color}.main`,
                        color: 'white',
                      }}
                    >
                      <Icon>{card.icon}</Icon>
                    </MKBox>
                  </MKBox>
                </Card>
              </Grid>
            ))}
          </Grid>

          {/* Main Content */}
          <Grid container spacing={3}>
            {/* Team Members Section */}
            <Grid item xs={12} md={4}>
              <Card sx={{ p: 3 }}>
                <MKBox display="flex" justifyContent="space-between" alignItems="center" mb={3}>
                  <MKTypography variant="h6">Team Members</MKTypography>
                  <Icon sx={{ cursor: 'pointer' }}>more_vert</Icon>
                </MKBox>
                {players.slice(0, 5).map((player) => (
                  <MKBox 
                    key={player.id} 
                    display="flex" 
                    alignItems="center" 
                    justifyContent="space-between"
                    mb={2}
                  >
                    <MKBox display="flex" alignItems="center" gap={2}>
                      <Avatar sx={{ bgcolor: 'error.main' }}>
                        {getInitials(player.fullName)}
                      </Avatar>
                      <MKBox>
                        <MKTypography variant="subtitle2">{player.fullName}</MKTypography>
                        <MKTypography variant="caption" color="text.secondary">
                          {player.position}
                        </MKTypography>
                      </MKBox>
                    </MKBox>
                    <MKBox display="flex" gap={1}>
                      <Icon sx={{ cursor: 'pointer' }}>twitter</Icon>
                      <Icon sx={{ cursor: 'pointer' }}>github</Icon>
                    </MKBox>
                  </MKBox>
                ))}
              </Card>
            </Grid>

            {/* Data Table Section */}
            <Grid item xs={12} md={8}>
              <Card sx={customStyles.card}>
                <MKBox display="flex" justifyContent="space-between" alignItems="center" mb={3}>
                  <MKTypography variant="h6">Latest News</MKTypography>
                  <MKBox>
                    <MKButton 
                      variant="text" 
                      color="error" 
                      size="small"
                      onClick={handleLogout}
                    >
                      Logout
                    </MKButton>
                  </MKBox>
                </MKBox>

                {isLoading ? (
                  <MKBox p={3} textAlign="center">
                    <MKTypography variant="body1">Loading data...</MKTypography>
                  </MKBox>
                ) : error ? (
                  <MKBox p={3} textAlign="center" color="error">
                    <MKTypography variant="body1">{error}</MKTypography>
                  </MKBox>
                ) : (
                  <DataTable 
                    value={activeTab === 0 ? players : sponsors}
                    paginator 
                    rows={isMobile ? 5 : 10}
                    filterDisplay={isMobile ? "menu" : "row"}
                    stripedRows
                    responsiveLayout="stack"
                    breakpoint="960px"
                    emptyMessage="No data found."
                    className="custom-datatable"
                    style={{ 
                      fontSize: isMobile ? '0.875rem' : '1rem',
                    }}
                    sx={customStyles.table}
                  >
                    <Column 
                      field="fullName" 
                      header="Player" 
                      body={isMobile ? mobileNameBodyTemplate : nameBodyTemplate}
                      sortable 
                      filter={!isMobile}
                      filterPlaceholder="Search by name"
                      style={{ minWidth: isMobile ? '200px' : '300px' }}
                    />
                    {!isMobile && (
                      <Column 
                        field="position" 
                        header="Position" 
                        sortable 
                        filter
                        filterPlaceholder="Search position"
                      />
                    )}
                    <Column 
                      field="paymentReference" 
                      header="Status" 
                      body={statusBodyTemplate}
                      sortable
                      style={{ width: isMobile ? '100px' : '150px' }}
                    />
                    {!isTablet && (
                      <Column 
                        field="Channel" 
                        header="Channel" 
                        sortable 
                        filter
                        filterPlaceholder="Search channel"
                      />
                    )}
                    {!isMobile && (
                      <Column 
                        field="created_at" 
                        header="Registration Date" 
                        body={dateBodyTemplate}
                        sortable
                      />
                    )}
                  </DataTable>
                )}
              </Card>
            </Grid>
          </Grid>
        </Container>
      </MKBox>
    </>
  );
}

export default Dashboard;
