import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";
import MKBox from "components/MKBox";
import MKTypography from "components/MKTypography";
import MKButton from "components/MKButton";
import Header from "components/Header";
import Footer from "components/Footer";
import { useState } from "react";
import TextField from "@mui/material/TextField";
import Checkbox from "@mui/material/Checkbox";
import FormControlLabel from "@mui/material/FormControlLabel";
import CircularProgress from "@mui/material/CircularProgress";

function BecomeSponsor() {
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    phoneNumber: "",
    emailUpdates: false,
    sponsorNumber: ""
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await fetch('https://x8ki-letl-twmt.n7.xano.io/api:TF3YOouP/kbfoundation', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData)
      });
      if (response.ok) {
        alert('Form submitted successfully!');
        setFormData({
          fullName: "",
          email: "",
          phoneNumber: "",
          emailUpdates: false,
          sponsorNumber: ""
        });
      }
    } catch (error) {
      alert('Error submitting form');
    }
    setLoading(false);
  };

  const handleChange = (e) => {
    const { name, value, checked, type } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  return (
    <>
      <Header />
      <MKBox sx={{ paddingTop: "60px" }} bgcolor="white">
        {/* Hero Section */}
        <MKBox
          minHeight="50vh"
          width="100%"
          sx={{
            backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)), url('/images/soccer-feet.jpg')`,
            backgroundSize: "cover",
            backgroundPosition: "center",
            display: "grid",
            placeItems: "center",
          }}
        >
          <Container>
            <Grid container justifyContent="center" textAlign="center">
              <Grid item xs={12} md={8}>
                <MKTypography variant="h1" color="white" mb={3}>
                  Become a Sponsor
                </MKTypography>
                <MKTypography variant="body1" color="white" mb={3}>
                  Help transform young lives through soccer
                </MKTypography>
              </Grid>
            </Grid>
          </Container>
        </MKBox>

        {/* Sponsorship Options */}
        <Container sx={{ py: 6 }}>
          <Grid container spacing={3}>
            <Grid item xs={12} md={4}>
              <Card sx={{ p: 3, height: "100%" }}>
                <MKTypography variant="h4" mb={2}>
                  Basic Sponsor
                </MKTypography>
                <MKTypography variant="h2" mb={2}>
                  $500
                </MKTypography>
                {/* Add sponsorship details */}
              </Card>
            </Grid>
            <Grid item xs={12} md={4}>
              <Card sx={{ p: 3, height: "100%" }}>
                <MKTypography variant="h4" mb={2}>
                  Premium Sponsor
                </MKTypography>
                <MKTypography variant="h2" mb={2}>
                  $1000
                </MKTypography>
                {/* Add sponsorship details */}
              </Card>
            </Grid>
            <Grid item xs={12} md={4}>
              <Card sx={{ p: 3, height: "100%" }}>
                <MKTypography variant="h4" mb={2}>
                  Elite Sponsor
                </MKTypography>
                <MKTypography variant="h2" mb={2}>
                  $2000
                </MKTypography>
                {/* Add sponsorship details */}
              </Card>
            </Grid>
          </Grid>
        </Container>

        {/* Sponsorship Form */}
        <Container sx={{ py: 6 }}>
          <Card sx={{ p: 4 }}>
            <MKTypography variant="h3" mb={3}>
              Sponsor a Player
            </MKTypography>
            <form onSubmit={handleSubmit}>
              <Grid container spacing={3}>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="Full Name"
                    name="fullName"
                    value={formData.fullName}
                    onChange={handleChange}
                    required
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    label="Email"
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                  />
                </Grid>
                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    label="Phone Number"
                    name="phoneNumber"
                    value={formData.phoneNumber}
                    onChange={handleChange}
                    required
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="Number of Players to Sponsor"
                    name="sponsorNumber"
                    type="number"
                    value={formData.sponsorNumber}
                    onChange={handleChange}
                    required
                  />
                </Grid>
                <Grid item xs={12}>
                  <FormControlLabel
                    control={
                      <Checkbox
                        name="emailUpdates"
                        checked={formData.emailUpdates}
                        onChange={handleChange}
                      />
                    }
                    label="Receive email updates about sponsored players"
                  />
                </Grid>
                <Grid item xs={12}>
                  <MKButton
                    type="submit"
                    variant="contained"
                    color="error"
                    fullWidth
                    disabled={loading}
                  >
                    {loading ? <CircularProgress size={24} color="inherit" /> : "Submit"}
                  </MKButton>
                </Grid>
              </Grid>
            </form>
          </Card>
        </Container>
      </MKBox>
      <Footer />
    </>
  );
}

export default BecomeSponsor;
