import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Container from "@mui/material/Container";
import Card from "@mui/material/Card";
import MKBox from "components/MKBox";
import MKTypography from "components/MKTypography";
import Avatar from "@mui/material/Avatar";
import Chip from "@mui/material/Chip";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import 'primereact/resources/themes/lara-light-indigo/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';
import useMediaQuery from '@mui/material/useMediaQuery';
import { useTheme } from '@mui/material/styles';
import MKButton from "components/MKButton";
import Grid from "@mui/material/Grid";
import PeopleIcon from '@mui/icons-material/People';
import HandshakeIcon from '@mui/icons-material/Handshake';
import MonetizationOnIcon from '@mui/icons-material/MonetizationOn';
import AccountBalanceWalletIcon from '@mui/icons-material/AccountBalanceWallet';
import AuthHeader from "components/AuthHeader";

function Dashboard() {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const isTablet = useMediaQuery(theme.breakpoints.down('md'));
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState(0);
  const [players, setPlayers] = useState([]);
  const [sponsors, setSponsors] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const authToken = localStorage.getItem("authToken");
    if (!authToken) {
      navigate("/auth");
      return;
    }

    const fetchData = async () => {
      try {
        setIsLoading(true);

        // Fetch players
        const playersResponse = await fetch(
          "https://x8ki-letl-twmt.n7.xano.io/api:TF3YOouP/kbfoundation_players",
          {
            headers: { Authorization: `Bearer ${authToken}` },
          }
        );

        // Fetch sponsors
        const sponsorsResponse = await fetch(
          "https://x8ki-letl-twmt.n7.xano.io/api:TF3YOouP/kbfoundation",
          {
            headers: { Authorization: `Bearer ${authToken}` },
          }
        );

        if (!playersResponse.ok || !sponsorsResponse.ok) {
          throw new Error("Failed to fetch data");
        }

        const playersData = await playersResponse.json();
        const sponsorsData = await sponsorsResponse.json();

        setPlayers(playersData);
        setSponsors(sponsorsData);
      } catch (err) {
        setError(err.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, [navigate]);

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  const getStatusColor = (paymentReference) => {
    return paymentReference ? "success" : "warning";
  };

  const getInitials = (name) => {
    return name
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase();
  };

  const amountBodyTemplate = (rowData) => {
    const amount = (rowData.sponsorNumber || 0) / 100;
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'GHS',
      minimumFractionDigits: 2,
    }).format(amount);
  };

  const statusBodyTemplate = (rowData) => {
    return (
      <Chip
        label={rowData.paymentReference ? "Paid" : "Pending"}
        color={getStatusColor(rowData.paymentReference)}
        size="small"
        sx={{
          borderRadius: '16px',
          fontSize: '0.75rem',
          fontWeight: 600
        }}
      />
    );
  };

  const nameBodyTemplate = (rowData) => {
    return (
      <MKBox display="flex" alignItems="center">
        <Avatar 
          sx={{ 
            bgcolor: activeTab === 0 ? 'error.main' : 'primary.main',
            width: 40,
            height: 40,
            marginRight: 2
          }}
        >
          {getInitials(rowData.fullName)}
        </Avatar>
        <MKBox>
          <MKTypography variant="subtitle2" fontWeight="medium">
            {rowData.fullName}
          </MKTypography>
          <MKTypography variant="caption" color="text.secondary">
            {rowData.email}
          </MKTypography>
        </MKBox>
      </MKBox>
    );
  };

  const dateBodyTemplate = (rowData) => {
    return new Date(rowData.created_at).toLocaleDateString();
  };

  const mobileNameBodyTemplate = (rowData) => {
    return (
      <MKBox display="flex" flexDirection="column" gap={1}>
        <MKBox display="flex" alignItems="center" gap={1}>
          <Avatar 
            sx={{ 
              bgcolor: activeTab === 0 ? 'error.main' : 'primary.main',
              width: 32,
              height: 32
            }}
          >
            {getInitials(rowData.fullName)}
          </Avatar>
          <MKTypography variant="subtitle2" fontWeight="medium">
            {rowData.fullName}
          </MKTypography>
        </MKBox>
        <MKBox pl={5}>
          <MKTypography variant="caption" color="text.secondary">
            {rowData.email}
          </MKTypography>
          {activeTab === 0 && (
            <MKTypography variant="caption" display="block" color="text.secondary">
              {rowData.position}
            </MKTypography>
          )}
        </MKBox>
      </MKBox>
    );
  };

  const getVisibleColumns = () => {
    if (isMobile) {
      return ['fullName', 'paymentReference'];
    }
    if (isTablet) {
      return ['fullName', 'paymentReference', 'created_at'];
    }
    return ['fullName', 'position', 'paymentReference', 'Channel', 'created_at'];
  };

  const customStyles = {
    header: {
      backgroundColor: theme.palette.background.paper,
      padding: theme.spacing(2),
      borderRadius: theme.shape.borderRadius,
      boxShadow: theme.shadows[2],
    },
    card: {
      overflow: 'hidden',
      border: 'none',
      boxShadow: theme.shadows[5],
      padding: theme.spacing(2),
    },
    tabs: {
      backgroundColor: theme.palette.background.paper,
      borderRadius: theme.shape.borderRadius,
      padding: theme.spacing(1),
      marginBottom: theme.spacing(3),
    },
    table: {
      '& .p-datatable-wrapper': {
        padding: theme.spacing(1),
      },
      '& .p-datatable-header': {
        padding: theme.spacing(2),
        backgroundColor: theme.palette.background.paper,
      },
      '& .p-datatable-tbody > tr > td': {
        padding: {
          xs: theme.spacing(1.5),
          sm: theme.spacing(2),
        },
      },
      '& .p-paginator': {
        padding: theme.spacing(2),
      },
      '& .p-datatable-footer': {
        padding: theme.spacing(2),
      },
    },
    mainHeader: {
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      zIndex: 1100,
      backgroundColor: theme.palette.background.paper,
      borderBottom: `1px solid ${theme.palette.divider}`,
      padding: theme.spacing(2),
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
    },
  };

  const handleLogout = () => {
    localStorage.removeItem("isAuthenticated");
    navigate("/auth");
  };

  const calculateStats = () => {
    const playerCount = players.length;
    const sponsorCount = sponsors.length;

    // Only count paid players (players with paymentReference)
    const paidPlayers = players.filter(player => player.paymentReference);
    const playerRevenue = paidPlayers.length * 465;

    // Calculate sponsor revenue in GHS (divided by 100)
    const sponsorRevenue = sponsors.reduce((total, sponsor) => 
      total + ((sponsor.sponsorNumber || 0) / 100), 0
    );

    return {
      playerCount,
      sponsorCount,
      playerRevenue,
      sponsorRevenue,
    };
  };

  const StatCard = ({ title, value, icon, color }) => (
    <Card sx={{ 
      p: 3, 
      height: '100%',
      display: 'flex',
      flexDirection: 'column',
      position: 'relative',
      overflow: 'hidden'
    }}>
      <MKBox 
        sx={{ 
          position: 'absolute',
          top: 0,
          right: 0,
          p: 2,
          opacity: 0.2
        }}
      >
        {icon}
      </MKBox>
      <MKTypography variant="h6" color="text.secondary" mb={1}>
        {title}
      </MKTypography>
      <MKTypography 
        variant="h4" 
        sx={{ 
          color: `${color}.main`,
          fontWeight: 'bold'
        }}
      >
        {typeof value === 'number' && (
          title.includes('Revenue') 
            ? new Intl.NumberFormat('en-US', {
                style: 'currency',
                currency: 'GHS',
                minimumFractionDigits: 2,
              }).format(value)
            : value.toLocaleString('en-US')
        )}
      </MKTypography>
    </Card>
  );

  return (
    <>
      <AuthHeader title="KB Foundation Dashboard" />
      <MKBox
        sx={{
          backgroundColor: "#f8f9fa",
          minHeight: '100vh',
          paddingTop: { xs: '100px', sm: '120px' },
          paddingBottom: { xs: '20px', sm: '30px' },
        }}
      >
        <Container maxWidth="xl">
          <Grid container spacing={3} mb={4}>
            <Grid item xs={12} sm={6} md={3}>
              <StatCard
                title="Total Players"
                value={calculateStats().playerCount}
                icon={<PeopleIcon sx={{ fontSize: 48 }} />}
                color="info"
              />
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <StatCard
                title="Total Sponsors"
                value={calculateStats().sponsorCount}
                icon={<HandshakeIcon sx={{ fontSize: 48 }} />}
                color="success"
              />
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <StatCard
                title="Player Revenue"
                value={calculateStats().playerRevenue}
                icon={<MonetizationOnIcon sx={{ fontSize: 48 }} />}
                color="warning"
              />
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <StatCard
                title="Sponsor Revenue"
                value={calculateStats().sponsorRevenue}
                icon={<AccountBalanceWalletIcon sx={{ fontSize: 48 }} />}
                color="error"
              />
            </Grid>
          </Grid>

          <Card sx={customStyles.card}>
            <Tabs 
              value={activeTab} 
              onChange={handleTabChange}
              variant={isMobile ? "fullWidth" : "standard"}
              sx={customStyles.tabs}
            >
              <Tab label="Players" />
              <Tab label="Sponsors" />
            </Tabs>

            {isLoading ? (
              <MKBox p={3} textAlign="center">
                <MKTypography variant="body1">Loading data...</MKTypography>
              </MKBox>
            ) : error ? (
              <MKBox p={3} textAlign="center" color="error">
                <MKTypography variant="body1">{error}</MKTypography>
              </MKBox>
            ) : activeTab === 0 ? (
              <DataTable 
                value={players} 
                paginator 
                rows={isMobile ? 5 : 10}
                filterDisplay={isMobile ? "menu" : "row"}
                stripedRows
                responsiveLayout="stack"
                breakpoint="960px"
                emptyMessage="No players found."
                className="custom-datatable"
                style={{ 
                  fontSize: isMobile ? '0.875rem' : '1rem',
                }}
                sx={customStyles.table}
              >
                <Column 
                  field="fullName" 
                  header="Player" 
                  body={isMobile ? mobileNameBodyTemplate : nameBodyTemplate}
                  sortable 
                  filter={!isMobile}
                  filterPlaceholder="Search by name"
                  style={{ minWidth: isMobile ? '200px' : '300px' }}
                />
                {!isMobile && (
                  <Column 
                    field="position" 
                    header="Position" 
                    sortable 
                    filter
                    filterPlaceholder="Search position"
                  />
                )}
                <Column 
                  field="paymentReference" 
                  header="Status" 
                  body={statusBodyTemplate}
                  sortable
                  style={{ width: isMobile ? '100px' : '150px' }}
                />
                {!isTablet && (
                  <Column 
                    field="Channel" 
                    header="Channel" 
                    sortable 
                    filter
                    filterPlaceholder="Search channel"
                  />
                )}
                {!isMobile && (
                  <Column 
                    field="created_at" 
                    header="Registration Date" 
                    body={dateBodyTemplate}
                    sortable
                  />
                )}
              </DataTable>
            ) : (
              <DataTable 
                value={sponsors} 
                paginator 
                rows={isMobile ? 5 : 10}
                filterDisplay={isMobile ? "menu" : "row"}
                stripedRows
                responsiveLayout="stack"
                breakpoint="960px"
                emptyMessage="No sponsors found."
                className="custom-datatable"
                style={{ 
                  fontSize: isMobile ? '0.875rem' : '1rem',
                }}
                sx={customStyles.table}
              >
                <Column 
                  field="fullName" 
                  header="Sponsor" 
                  body={isMobile ? mobileNameBodyTemplate : nameBodyTemplate}
                  sortable 
                  filter={!isMobile}
                  filterPlaceholder="Search by name"
                  style={{ minWidth: isMobile ? '200px' : '300px' }}
                />
                {!isMobile && (
                  <Column 
                    field="phoneNumber" 
                    header="Phone Number" 
                    sortable 
                    filter
                    filterPlaceholder="Search phone"
                  />
                )}
                <Column 
                  field="sponsorNumber" 
                  header="Amount" 
                  body={amountBodyTemplate}
                  sortable
                />
                <Column 
                  field="paymentReference" 
                  header="Status" 
                  body={statusBodyTemplate}
                  sortable
                />
                {!isMobile && (
                  <Column 
                    field="created_at" 
                    header="Registration Date" 
                    body={dateBodyTemplate}
                    sortable
                  />
                )}
              </DataTable>
            )}
          </Card>
        </Container>
      </MKBox>
    </>
  );
}

export default Dashboard;
