import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Container from "@mui/material/Container";
import Card from "@mui/material/Card";
import MKBox from "components/MKBox";
import MKTypography from "components/MKTypography";
import Header from "components/Header";
import Avatar from "@mui/material/Avatar";
import Chip from "@mui/material/Chip";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Card as PCard } from 'primereact/card';
import { Divider } from 'primereact/divider';
import 'primereact/resources/themes/lara-light-indigo/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';
import useMediaQuery from '@mui/material/useMediaQuery';
import { useTheme } from '@mui/material/styles';
import MKButton from "components/MKButton";
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import PeopleIcon from '@mui/icons-material/People';
import CommentIcon from '@mui/icons-material/Comment';
import AttachMoneyIcon from '@mui/icons-material/AttachMoney';

function Dashboard() {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const isTablet = useMediaQuery(theme.breakpoints.down('md'));
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState(0);
  const [players, setPlayers] = useState([]);
  const [sponsors, setSponsors] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const authToken = localStorage.getItem("authToken");
    if (!authToken) {
      navigate("/auth");
      return;
    }

    const fetchData = async () => {
      try {
        setIsLoading(true);

        // Fetch players
        const playersResponse = await fetch(
          "https://x8ki-letl-twmt.n7.xano.io/api:TF3YOouP/kbfoundation_players",
          {
            headers: { Authorization: `Bearer ${authToken}` },
          }
        );

        // Fetch sponsors
        const sponsorsResponse = await fetch(
          "https://x8ki-letl-twmt.n7.xano.io/api:TF3YOouP/kbfoundation",
          {
            headers: { Authorization: `Bearer ${authToken}` },
          }
        );

        if (!playersResponse.ok || !sponsorsResponse.ok) {
          throw new Error("Failed to fetch data");
        }

        const playersData = await playersResponse.json();
        const sponsorsData = await sponsorsResponse.json();

        setPlayers(playersData);
        setSponsors(sponsorsData);
      } catch (err) {
        setError(err.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, [navigate]);

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  const getStatusColor = (paymentReference) => {
    return paymentReference ? "success" : "warning";
  };

  const getInitials = (name) => {
    return name
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase();
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const statusBodyTemplate = (rowData) => {
    return (
      <Chip
        label={rowData.paymentReference ? "Paid" : "Pending"}
        color={getStatusColor(rowData.paymentReference)}
        size="small"
        sx={{
          borderRadius: '16px',
          fontSize: '0.75rem',
          fontWeight: 600
        }}
      />
    );
  };

  const nameBodyTemplate = (rowData) => {
    return (
      <MKBox display="flex" alignItems="center">
        <Avatar 
          sx={{ 
            bgcolor: activeTab === 0 ? 'error.main' : 'primary.main',
            width: 40,
            height: 40,
            marginRight: 2
          }}
        >
          {getInitials(rowData.fullName)}
        </Avatar>
        <MKBox>
          <MKTypography variant="subtitle2" fontWeight="medium">
            {rowData.fullName}
          </MKTypography>
          <MKTypography variant="caption" color="text.secondary">
            {rowData.email}
          </MKTypography>
        </MKBox>
      </MKBox>
    );
  };

  const dateBodyTemplate = (rowData) => {
    return new Date(rowData.created_at).toLocaleDateString();
  };

  const amountBodyTemplate = (rowData) => {
    return formatCurrency(rowData.sponsorNumber);
  };

  const mobileNameBodyTemplate = (rowData) => {
    return (
      <MKBox display="flex" flexDirection="column" gap={1}>
        <MKBox display="flex" alignItems="center" gap={1}>
          <Avatar 
            sx={{ 
              bgcolor: activeTab === 0 ? 'error.main' : 'primary.main',
              width: 32,
              height: 32
            }}
          >
            {getInitials(rowData.fullName)}
          </Avatar>
          <MKTypography variant="subtitle2" fontWeight="medium">
            {rowData.fullName}
          </MKTypography>
        </MKBox>
        <MKBox pl={5}>
          <MKTypography variant="caption" color="text.secondary">
            {rowData.email}
          </MKTypography>
          {activeTab === 0 && (
            <MKTypography variant="caption" display="block" color="text.secondary">
              {rowData.position}
            </MKTypography>
          )}
        </MKBox>
      </MKBox>
    );
  };

  const getVisibleColumns = () => {
    if (isMobile) {
      return ['fullName', 'paymentReference'];
    }
    if (isTablet) {
      return ['fullName', 'paymentReference', 'created_at'];
    }
    return ['fullName', 'position', 'paymentReference', 'Channel', 'created_at'];
  };

  const customStyles = {
    header: {
      backgroundColor: theme.palette.background.paper,
      padding: theme.spacing(2),
      borderRadius: theme.shape.borderRadius,
      boxShadow: theme.shadows[2],
    },
    card: {
      overflow: 'hidden',
      border: 'none',
      boxShadow: theme.shadows[5],
      padding: theme.spacing(2),
    },
    tabs: {
      backgroundColor: theme.palette.background.paper,
      borderRadius: theme.shape.borderRadius,
      padding: theme.spacing(1),
      marginBottom: theme.spacing(3),
    },
    table: {
      '& .p-datatable-wrapper': {
        padding: theme.spacing(1),
      },
      '& .p-datatable-header': {
        padding: theme.spacing(2),
        backgroundColor: theme.palette.background.paper,
      },
      '& .p-datatable-tbody > tr > td': {
        padding: {
          xs: theme.spacing(1.5),
          sm: theme.spacing(2),
        },
      },
      '& .p-paginator': {
        padding: theme.spacing(2),
      },
      '& .p-datatable-footer': {
        padding: theme.spacing(2),
      },
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("isAuthenticated");
    navigate("/auth");
  };

  const statsData = {
    orders: {
      count: 152,
      newCount: 24,
      label: "Orders",
      subtext: "since last visit",
      icon: <ShoppingCartIcon />,
    },
    revenue: {
      count: "$2,100",
      percentage: 52,
      label: "Revenue",
      subtext: "since last week",
      icon: <AttachMoneyIcon />,
    },
    customers: {
      count: "28,441",
      newCount: 520,
      label: "Customers",
      subtext: "newly registered",
      icon: <PeopleIcon />,
    },
    comments: {
      count: "152",
      responded: 85,
      label: "Comments",
      subtext: "responded",
      icon: <CommentIcon />,
    },
  };

  const StatCard = ({ data }) => (
    <PCard className="stats-card">
      <div className="flex justify-content-between align-items-start">
        <div>
          <h2 className="text-900 font-bold text-xl mb-2">{data.count}</h2>
          <span className="text-600 font-medium">{data.label}</span>
          <div className="mt-2">
            <span className="text-green-500 font-medium">
              {data.newCount || data.percentage}
              {data.percentage ? '%+' : ' new'} 
            </span>
            <span className="text-500"> {data.subtext}</span>
          </div>
        </div>
        <div className="stats-icon">
          {data.icon}
        </div>
      </div>
    </PCard>
  );

  return (
    <>
      <Header />
      <MKBox
        sx={{
          backgroundColor: "#f8f9fa",
          minHeight: '100vh',
          paddingTop: { xs: '60px', sm: '70px' },
          paddingBottom: { xs: '20px', sm: '30px' },
        }}
      >
        <Container maxWidth="xl">
          <MKBox sx={customStyles.header} mb={3}>
            <div className="flex justify-content-between align-items-center">
              <div className="flex align-items-center">
                <Avatar 
                  sx={{ 
                    width: 56, 
                    height: 56, 
                    marginRight: 2 
                  }}
                  src="/path-to-profile-image.jpg"
                />
                <div>
                  <MKTypography variant="h4">Kathryn Murphy</MKTypography>
                  <div className="flex gap-3 text-500">
                    <span>FOLLOWERS 333</span>
                    <span>PROJECTS 26</span>
                    <span>COLLECTIONS 17</span>
                    <span>SHOTS 130</span>
                  </div>
                </div>
              </div>
              <MKButton
                variant="outlined"
                color="error"
                onClick={handleLogout}
                size={isMobile ? "small" : "medium"}
              >
                Logout
              </MKButton>
            </div>
          </MKBox>

          <div className="grid">
            <div className="col-12 md:col-6 lg:col-3">
              <StatCard data={statsData.orders} />
            </div>
            <div className="col-12 md:col-6 lg:col-3">
              <StatCard data={statsData.revenue} />
            </div>
            <div className="col-12 md:col-6 lg:col-3">
              <StatCard data={statsData.customers} />
            </div>
            <div className="col-12 md:col-6 lg:col-3">
              <StatCard data={statsData.comments} />
            </div>
          </div>

          <div className="grid mt-4">
            <div className="col-12 lg:col-8">
              <PCard title="Latest News" className="h-full">
                <div className="flex flex-column gap-4">
                  <div>
                    <h3>Aenean euismod elementum</h3>
                    <p className="text-500">
                      Vitae turpis massa sed elementum tempus egestas sed sed risus.
                    </p>
                  </div>
                  <Divider />
                  <div>
                    <h3>In iaculis nunc sed augue lacus</h3>
                    <p className="text-500">
                      Viverra vitae congue eu consequat ac felis donec.
                    </p>
                  </div>
                  <Divider />
                  <div>
                    <h3>Proin sagittis nisl rhoncus</h3>
                    <p className="text-500">
                      In pellentesque massa placerat duis ultricies lacus.
                    </p>
                  </div>
                </div>
              </PCard>
            </div>
            <div className="col-12 lg:col-4">
              <PCard title="Team Members" className="h-full">
                <div className="flex flex-column gap-3">
                  {[
                    { name: "Janette Hudson", role: "UI/UX Designer" },
                    { name: "Theresa Webb Hudson", role: "UI/UX Designer" },
                    { name: "Arlene McCoy", role: "UI/UX Designer" },
                    { name: "Jacob Jones", role: "UI/UX Designer" },
                    { name: "James Cooper", role: "UI/UX Designer" },
                  ].map((member) => (
                    <div key={member.name} className="flex align-items-center justify-content-between p-2">
                      <div className="flex align-items-center gap-2">
                        <Avatar>{getInitials(member.name)}</Avatar>
                        <div>
                          <MKTypography variant="subtitle2">{member.name}</MKTypography>
                          <MKTypography variant="caption" color="text.secondary">
                            {member.role}
                          </MKTypography>
                        </div>
                      </div>
                      <div className="flex gap-2">
                        <i className="pi pi-twitter"></i>
                        <i className="pi pi-github"></i>
                        <i className="pi pi-facebook"></i>
                      </div>
                    </div>
                  ))}
                </div>
              </PCard>
            </div>
          </div>
        </Container>
      </MKBox>
    </>
  );
}

export default Dashboard;
